public boolean validate()
{
    boolean isValid = super();
    int thisYear = year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    int mm;
    int yy;

    if (isValid)
    {
        [mm, yy] = element.parseMonthYear(this.text());
        isValid = mm != 0;
        if (!isValid)
        {
            error(strFmt("@SYS4040005", thisYear, thisYear + #CreditCardExpirationYearsToDisplay));
        }
    }

    return isValid;
}

