[Form]
public class CustAdvanceInvoiceSettlement_W extends FormRun
{
    #ISOCountryRegionCodes
    boolean isCZ, isHU, isPL, isEE, isLT, isLV, isRU;

    RecId advanceRecId,
          reversalRecId,
          invoiceRecId,
          cancellationRecId,
          creditNoteRecId;

}

public void init()
{
    this.initFromCaller();

    isPL = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISOPL]);
    isCZ = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISOCZ]);
    isHU = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISOHU]);
    isEE = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISOEE]);
    isLT = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISOLT]);
    isLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISOLV]);
    isRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#ISORU]);

    super();
}

void initFromCaller()
{
    CustTrans                 localCustTrans;
    CzCustAdvanceInvoiceTable localAdvanceInvoiceTable;
    CustInvoiceJour           localCustInvoiceJour;

    if (! element.args() ||
        ! element.args().record())
        throw error ("@GLS103799");

    advanceRecId      = 0;
    reversalRecId     = 0;
    invoiceRecId      = 0;
    cancellationRecId = 0;
    creditNoteRecId   = 0;

    switch (element.args().record().TableId)
    {
        case tableNum(CustTrans) :
            localCustTrans = element.args().record();

            localAdvanceInvoiceTable = CzCustAdvanceInvoiceTable::findVoucherDate(localCustTrans.Voucher, localCustTrans.TransDate);
            if (localAdvanceInvoiceTable)
            {
                switch (localAdvanceInvoiceTable.Type)
                {
                    case AdvanceInvoiceType_W::Reversal             :
                        reversalRecId = localAdvanceInvoiceTable.RecId;
                        break;

                    case AdvanceInvoiceType_W::ReversalCancellation :
                        cancellationRecId = localAdvanceInvoiceTable.RecId;
                        break;

                    case AdvanceInvoiceType_W::Common               :
                    case AdvanceInvoiceType_W::CreditNote           :
                        advanceRecId = localAdvanceInvoiceTable.RecId;
                        break;

                    default :
                        throw error(Error::wrongUseOfFunction(funcName()));
                }
            }
            else
            {
                localCustInvoiceJour = CustInvoiceJour::findFromCustTransVoucher(localCustTrans.Invoice,
                                                                                 localCustTrans.Voucher,
                                                                                 localCustTrans.TransDate,
                                                                                 localCustTrans.AccountNum);
                if (localCustInvoiceJour.creditNote())
                {
                    creditNoteRecId = localCustInvoiceJour.RecId;
                }
                else
                {
                    invoiceRecId    = localCustInvoiceJour.RecId;
                }
            }

            break;

        default                  :
            throw error ("@GLS103799");
    }
}

public void setRanges(Query _query)
{
    boolean                 foundCurrent;
    QueryBuildDataSource    qbds;
    int                     cnt;
    const string50          datasourceInvoiceJour           = 'CustInvoiceJour';
    const string50          datasourceAdvanceInvoice        = 'CustAdvanceInvoice';
    const string50          datasourceAdvanceReversal       = 'CustAdvanceReversal';
    const string50          datasourceCreditNote            = 'CreditNote';
    const string50          datasourceAdvanceCancellation   = 'CustAdvanceCancellation';

    void val(boolean _found, RecId _recId, FieldId _fieldId)
    {
        QueryBuildRange range = findOrCreateRange_W(qbds, _fieldId);

        range.value (_found                                                          ?
                     (_recId ? SysQuery::value(_recId) : SysQuery::valueUnlimited()) :
                     SysQuery::valueEmptyString());
        range.status(RangeStatus::Locked);
    }

    for (cnt = 1; cnt <= _query.dataSourceCount(); cnt ++)
    {
        qbds  = _query.dataSourceNo(cnt);
        if (qbds.table() == tableNum(CustAdvanceInvoiceSettlement_W))
        {
            switch (qbds.parentDataSource().name())
            {
                case datasourceInvoiceJour         :
                case datasourceAdvanceInvoice      :
                case datasourceAdvanceReversal     :
                    foundCurrent = advanceRecId || invoiceRecId || reversalRecId;
                    break;

                case datasourceCreditNote          :
                case datasourceAdvanceCancellation :
                    foundCurrent = creditNoteRecId || cancellationRecId;
                    break;
                default :
                    throw error(Error::wrongUseOfFunction(funcName()));
            }
            val(foundCurrent, advanceRecId,      fieldNum(CustAdvanceInvoiceSettlement_W, AdvanceInvoiceRecId));
            val(foundCurrent, invoiceRecId,      fieldNum(CustAdvanceInvoiceSettlement_W, InvoiceJourRecId));
            val(foundCurrent, reversalRecId,     fieldNum(CustAdvanceInvoiceSettlement_W, ReversalAdvanceInvoiceRecId));
            val(foundCurrent, creditNoteRecId,   fieldNum(CustAdvanceInvoiceSettlement_W, CreditNoteRecId));
            val(foundCurrent, cancellationRecId, fieldNum(CustAdvanceInvoiceSettlement_W, ReversalCancellationRecId));
        }
    }
}

public void init()
{
    super();

    custTrans_DS.query(new Query(queryStr(AdvanceInvoiceTransCust_W)));
    element.setRanges(custTrans_DS.query());
}

public void linkActive()
{
    element.initFromCaller();
    element.setRanges(custTrans_DS.query());
    element.setRanges(generalJournal_ds.query());
    element.setRanges(taxTrans_DS.query());

    super();

    generalJournal_DS.executeQuery();
    taxTrans_DS.executeQuery();
}

