}

display AmountCur taxInCostPrice(TaxTrans _taxTrans)
{
    if (_taxTrans.TaxInCostPriceRegulated)
        return _taxTrans.TaxInCostPriceRegulated;
    return _taxTrans.TaxInCostPrice;
}

public void executeQuery()
{
    QueryBuildDataSource ds;
    QueryBuildRange r;

    ds = this.query().dataSourceTable(tableNum(GeneralJournalSubledgerVoucher));
    r = ds.addRange(fieldNum(GeneralJournalSubledgerVoucher, Ledger));
    r.value(SysQuery::value(Ledger::current()));

    super();
}
