[Form]
public class CustAutomationStrategySetup extends FormRun implements ProcessScheduleParametersIWrite, ProcessScheduleParametersIInitialize, IRefreshable
{
    private ProcessScheduleSchedulingContract processScheduleSchedulingContract;
    private NoYes isActivity;
    private NoYes isEmail;
    private NoYes isCollectionLetter;

}

[Hookable(false)]
public void init()
{
    super();

    CustAutomationStrategy_Prediction.enabled(CustAutomationParameters::find().EnablePaymentPredictions);
}

/// <summary>
/// Writes parameters when creating a new series.
/// </summary>
[Hookable(false)]
public void createScheduleSeries(ProcessScheduleSchedulingContract _schedulingContract)
{
    CustAutomationCriteria::insertFromScheduleSeries(custAutomationStrategy, _schedulingContract);
    this.saveFormStateForProcessWizard();
}

/// <summary>
/// Writes parameters when updating an existing series.
/// </summary>
[Hookable(false)]
public void createScheduledOccurrence(ProcessScheduleSchedulingContract _schedulingContract)
{
    this.saveFormStateForProcessWizard();
}

/// <summary>
/// Writes parameters when creating a new occurrence.
/// </summary>
[Hookable(false)]
public void updateScheduleSeries(ProcessScheduleSchedulingContract _schedulingContract)
{
    this.updateCriteriaWithStrategyName(CustAutomationCriteria::findBySeries(_schedulingContract.getProcessScheduleSeriesData().getSeriesRecId(), NoYes::Yes));
    this.saveFormStateForProcessWizard();
}

/// <summary>
/// Writes parameters when updating an existing occurrence.
/// </summary>
[Hookable(false)]
public void updateScheduledOccurrence(ProcessScheduleSchedulingContract _schedulingContract)
{
    this.updateCriteriaWithStrategyName(CustAutomationCriteria::findByOccurrence(_schedulingContract.getProcessScheduleOccurrenceData().getOccurrenceRecId(), NoYes::Yes));
    this.saveFormStateForProcessWizard();
}

