[Hookable(false)]
public void setSchedulingContract(ProcessScheduleSchedulingContract _schedulingContract)
{
    processScheduleSchedulingContract = _schedulingContract;
}

/// <summary>
/// Initializes parameters for creating a new series.
/// </summary>
[Hookable(false)]
public void initializeForSeriesCreate()
{
}

/// <summary>
/// Initializes parameters for updating an existing series.
/// </summary>
[Hookable(false)]
public void initializeForSeriesUpdate()
{
    this.filterToStrategy(processScheduleSchedulingContract.getProcessScheduleSeriesData().getSeriesRecId(), ProcessScheduleSeriesOccurrence::Series);
}

/// <summary>
/// Initializes parameters for creating a new occurrence.
/// </summary>
[Hookable(false)]
public void initializeForOccurrenceCreate()
{
}

/// <summary>
/// Initializes parameters for updating an existing occurrence.
/// </summary>
[Hookable(false)]
public void initializeForOccurrenceUpdate()
{
    this.filterToStrategy(processScheduleSchedulingContract.getProcessScheduleOccurrenceData().getOccurrenceRecId(), ProcessScheduleSeriesOccurrence::Occurrence);
}

private void saveFormStateForProcessWizard()
{
    for (int i = 1; i < element.dataSourceCount(); i++)
    {
        try
        {
            if (!this.skipWrite(element.dataSource(i)) && element.dataSource(i).validateWrite())
            {
                element.dataSource(i).write();
            }
        }
        catch
        {
            exceptionTextFallThrough();
        }
    }
}

private boolean skipWrite(FormObjectSet datasource)
{
    if (!isActivity && dataSource.name() == tableStr(CustAutomationActivityTemplate))
    {
        return true;
    }

    if (!isEmail && dataSource.name() == tableStr(SysEmailSystemTable))
    {
        return true;
    }

    return false;
}

private void filterToStrategy(RefRecId _refRecId, ProcessScheduleSeriesOccurrence _scheduleType)
{
    CustAutomationCriteria criteria = _scheduleType == ProcessScheduleSeriesOccurrence::Occurrence
        ? CustAutomationCriteria::findByOccurrence(_refRecId)
        : CustAutomationCriteria::findBySeries(_refRecId);

    QueryBuildDataSource qbds = custAutomationStrategy_ds.query().dataSourceTable(tableNum(CustAutomationStrategy));
    qbds.addRange(fieldNum(CustAutomationStrategy, StrategyName)).value(SysQuery::value(criteria.StrategyName));
}

private void updateCriteriaWithStrategyName(CustAutomationCriteria _criteria)
{
    if (_criteria)
    {
        _criteria.StrategyName = custAutomationStrategy.StrategyName;
        _criteria.selectForUpdate(NoYes::Yes);

        ttsbegin;
        _criteria.update();
        ttscommit;
    }
}

internal display str showDocumentIcon(Common _common)
{
    return DocuRef::exist(_common.DataAreaId, _common.TableId, _common.RecId) ? 'GenericDocument' : 'PlannedDocument';
}

private void showDocuments(Common _record)
{
    if (_record)
    {
        Args args = new Args();
        args.record(_record);
        new MenuFunction(menuItemDisplayStr(DocuAttachment), MenuItemType::Display).run(args);
    }
}

[Hookable(false)]
public void refresh()
{
    custAutomationStrategyLine_ds.research(true);
}

private void modifyFormState()
{
    this.setActionTypes();

    this.setGlobalFormState();

    if (isActivity)
    {
        this.setFormStateForActivity();
    }

    if (isEmail)
    {
        this.setFormStateForEmail();
    }

    if (isCollectionLetter)
    {
        this.setFormStateForCollectionLetter();   
    }
}

private void setGlobalFormState()
{
    ActivityTemplateBusinessDocument.visible(isActivity);
    EmailBusinessDoc.visible(isEmail);
    EmailLineView.visible(isEmail);

    CustFormUtils::enableDisableDataSource(custAutomationActivityTemplate_ds, tableNum(CustAutomationActivityTemplate), isActivity);
    CustFormUtils::enableDisableDataSource(sysEmailSystemTable_ds, tableNum(SysEmailSystemTable), isEmail);
    CustFormUtils::enableDisableDataSource(SysEmailSystemMessage_ds, tableNum(SysEmailMessageSystemTable), isEmail);
}

private void setActionTypes()
{
    isActivity = custAutomationStrategyLine.ActionType == CustAutomationActionType::Activity ? NoYes::Yes : NoYes::No;
    isEmail = custAutomationStrategyLine.ActionType == CustAutomationActionType::Email ? NoYes::Yes : NoYes::No;
    isCollectionLetter = custAutomationStrategyLine.ActionType == CustAutomationActionType::CollectionLetter ? NoYes::Yes : NoYes::No;
}

private void setFormStateForActivity()
{
    custAutomationActivityTemplate_ds.research();
    ActivityTemplateBusinessDocument.caption(strFmt("@CustAutomation:StrategySetupChangeTabPage", "@CustAutomation:BusinessDocumentActivity", custAutomationStrategy.StrategyName));

    custAutomationStrategyLine.Recipient = CustAutomationRecipient::None;
    CustAutomationStrategyLine_BusinessPurposeContact.allowEdit(false);
    CustAutomationStrategyLine_Recipient.allowEdit(false);
    CustAutomationStrategyLine_BusinessDocument.allowEdit(true);
}

private void setFormStateForEmail()
{
    sysEmailSystemTable_ds.research();
    SysEmailSystemMessage_ds.research();
    this.resetStrategyDetailsGridFields();

    EmailBusinessDoc.caption(strFmt("@CustAutomation:StrategySetupChangeTabPage", "@CustAutomation:BusinessDocumentEmail", custAutomationStrategy.StrategyName));
    EmailLineView.caption(strFmt("@CustAutomation:StrategySetupChangeTabPage", "@CustAutomation:BusinessDocumentEmail", custAutomationStrategy.StrategyName));
}

private void setFormStateForCollectionLetter()
{
    custAutomationStrategyLine.Recipient = CustAutomationRecipient::None;
    CustAutomationStrategyLine_BusinessPurposeContact.allowEdit(false);
    CustAutomationStrategyLine_Recipient.allowEdit(false);
    CustAutomationStrategyLine_BusinessDocument.allowEdit(false);
}

private void resetStrategyDetailsGridFields()
{
    CustAutomationStrategyLine_BusinessPurposeContact.allowEdit(true);
    CustAutomationStrategyLine_Recipient.allowEdit(true);
    CustAutomationStrategyLine_BusinessDocument.allowEdit(true);
}

private void setFormStateForProcess()
{
    if(CustAutomationStrategy.CustExclude == CustAutomationExcludeBy::None)
    {
        CustAutomationStrategy_CustExcludeAmount.allowEdit(false);
    }
    else
    {
        CustAutomationStrategy_CustExcludeAmount.allowEdit(true);
    }

}

private void enableDisableDataSource(FormDataSource _dataSource, TableId _tableId, NoYes _enabled)
{
    if (_dataSource)
    {
        _dataSource.query().dataSourceTable(_tableId).enabled(_enabled);
    }
}

private void rereadRefreshDataSource(FormDataSource _dataSource)
{
    _dataSource.reread();
    _dataSource.refresh();
}

private void showEmailBody()
{
    FormRun formRun;
    Args args = new Args();

    args.caller(this);
    args.name(formstr(SysInetHTMLEditorRnr));
    if (sysEmailSystemMessage.LanguageId)
    {
        args.record(sysEmailSystemMessage);
        args.parm('0');
    }
    else
    {
        throw error("@SYS93670");
    }

    // Initialize editor form, run and wait
    formRun = ClassFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    sysEmailSystemMessage_DS.research();
}

