public void initValue()
{
    super();

    custAutomationStrategyLine.ParentRecId = custAutomationStrategy.RecId;

    if(custAutomationStrategyLine.ActionType == CustAutomationActionType::Activity)
    {

        select count(RecId) from custAutomationActivityTemplate;

        if(custAutomationActivityTemplate.RecId)
        {
            select firstonly TemplateName from custAutomationActivityTemplate;

            custAutomationStrategyLine.BusinessDocument = custAutomationActivityTemplate.TemplateName;
        }
        else
        {
            custAutomationStrategyLine.BusinessDocument = '';
        }
    }
}

