public class CustBankAccounts extends FormRun implements OfficeIGeneratedWorkbookCustomExporter
{
    LogisticsPostalAddressFormHandler addressController;
    CustBankAccountsBusinessLogic logicAndState;

    #ISOCountryRegionCodes

}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret && custBankAccount.RecId)
    {
        //When the bank account address is created/changed, different business rules apply for that address.
        //We need to ensure the bank account is left in a good state before the form is closed.
        ret = custBankAccount.validateWrite();
    }

    return ret;
}

/// <summary>
/// Gets the <c>CustBankAccountsBusinessLogic</c> object associated with this form.
/// </summary>
/// <returns>The <c>CustBankAccountsBusinessLogic</c> object associated with this form.</returns>
public CustBankAccountsBusinessLogic getLogicAndState()
{
    return logicAndState;
}

public void init()
{
    if (element.args())
    {
        element.args().applyRecordAsDynalink(true);
    }

    super();

    logicAndState = CustBankAccountsBusinessLogic::construct();
    addressController = LogisticsPostalAddressFormHandler::newParameters(custBankAccount_ds, logisticsPostalAddress_ds);
    addressController.callerUpdateQuery(fieldNum(CustBankAccount,Location));
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        addressController.callerActive();
        addressController.callerUpdateButtons(newAddress, editAddress, clearAddress, mapButton);

        element.updateDesign();
    }

    return ret;
}

public void init()
{
    QueryBuildRange qbrBank;
    QueryBuildRange qbrCust;
    RecId ledgerDimension;

    super();

    switch (element.args().dataset())
    {
        case tableNum(LedgerJournalTrans):
            this.query().dataSourceNo(1).clearDynalinks();
            qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(CustBankAccount, AccountID));
            qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(LedgerJournalTrans,CustVendBankAccountId))));

            qbrCust = this.query().dataSourceNo(1).addRange(fieldNum(CustBankAccount, CustAccount));

            ledgerDimension = element.args().record().(fieldName2id(element.args().dataset(),fieldStr(LedgerJournalTrans,LedgerDimension)));
            qbrCust.value(LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(ledgerDimension));

            break;

        case tableNum(SalesParmTable) :
            qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(CustBankAccount, AccountID));
            qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(SalesParmTable,CustBankAccountId))));

            qbrCust = this.query().dataSourceNo(1).addRange(fieldNum(CustBankAccount, CustAccount));
            qbrCust.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(SalesParmTable,InvoiceAccount))));

            break;
    }
}

