/// <summary>
/// Determines whether the form can be closed.
/// </summary>
/// <returns>
/// true if the form can be closed; otherwise, false.
/// </returns>

public boolean canClose()
{
    boolean canClose;
    DialogButton            dialogButton = DialogButton::No;
    canClose = super();

    //if the selected tab is PageLedger
    if ( canClose )
    {
        canClose = custBillingCodeVersion.validateLedgerAccounts();

        if ( !canClose )
        {
            billingCodeTable_ds.executeQuery();
        }
    }

    return canClose;
}

/// <summary>
/// Sets the accessibility of action buttons.
/// </summary>

void enableButtons()
{
    boolean    enableButtons;

    if (billingCodeTable_ds.getFirst())
    {
        enableButtons = true;
    }

    newVersion.enabled(enableButtons);

    //Enable/Disable any non-new related buttons
    if (billingCodeTable.RecId)
    {
        enableButtons = true;
    }
    else
    {
        enableButtons = false;
    }

    newVersion.enabled(enableButtons);
    DeleteBillingCode.enabled(enableButtons);

    if (enableButtons)
    {
        btnRemoveLedgerAcc.enabled((CustBillingCodeLedgerAccount::countRecords(custBillingCodeVersion.RecId) > 0));
        buttonRemove.enabled((CustBillingCodeCustomField::getMaxSequenceNumber(custBillingCodeVersion.RecId) > 0));
    }

    enableButtons = false;

    if (billingCodeTable.RecId && billingCodeTable_ds.getFirst() && sysDateEffectiveDataManager.canActivate(custBillingCodeVersion)
        && !sysDateEffectiveDataManager.existActiveVersion(billingCodeTable.RecId))
    {
        enableButtons = true;
    }

    activate.enabled(enableButtons);
    enableButtons = false;
}

