public void modifyMoveButtons()
{
    buttonMoveUp.enabled(false);
    buttonMoveDown.enabled(false);

    if (custBillingCodeCustomField.RecId)
    {
        if (custBillingCodeCustomField.RowCount() == 0)
        {
            buttonMoveUp.enabled(false);
            buttonMoveDown.enabled(false);
        }
        if (custBillingCodeCustomField.SequenceNumber == 1)
        {
            buttonMoveUp.enabled(false);
            if (maxSequenceNumber == 1)
            {
                buttonMoveDown.enabled(false);
            }
            else
            {
                buttonMoveDown.enabled(true);
            }
        }
        else if (custBillingCodeCustomField.SequenceNumber == maxSequenceNumber)
        {
            buttonMoveUp.enabled(true);
            buttonMoveDown.enabled(false);
        }
        else
        {
            buttonMoveUp.enabled(true);
            buttonMoveDown.enabled(true);
        }
    }
}

/// <summary>
/// Deletes the the selected record and resets the number sequence.
/// </summary>

public void removeCustomFieldBtnClicked()
{
    // consider moving the below logic to Table
    CustBillingCodeCustomField billingCodeCustomField;
    CustomFieldSequenceNo sequenceNumber = 1;

    if (custBillingCodeCustomField.validateDelete())
    {
        ttsbegin;
        custBillingCodeCustomField.delete();
        while select forupdate RecId
            from
                billingCodeCustomField
            order by
                SequenceNumber asc
            where
                billingCodeCustomField.CustBillingCodeVersion == custBillingCodeVersion.RecId
        {
            billingCodeCustomField.SequenceNumber = sequenceNumber;
            billingCodeCustomField.update();
            sequenceNumber++;
        }
        ttscommit;
        custBillingCodeCustomField_ds.executeQuery();
        maxSequenceNumber = CustBillingCodeCustomField::getMaxSequenceNumber(custBillingCodeVersion.RecId);
        if (maxSequenceNumber <= 0)
        {
            buttonRemove.enabled(false);
        }
    }
}

