/// <summary>
/// Validates the billing code record.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
///<remarks>
/// Shows warning message if <c>LedgerAccount</c> is empty and <c>AllowChangeLedgerAccount</c> is not checked.
///</remarks>

public boolean validateWrite()
{
    boolean             ret = true;
    str                 script;
    Args                args;
    FormRun             formRun;
    NoYesId             modifyExistingRecord;
    NoYesId             createNewRecord;
    NoYesId             activateExistingRecord;
    CustBillingCode     localCustBillingCode;
    CustBillingCodeRate custBillingCodeRate;
    ISysDateEffectiveFutureVersionOptions   formSysDateEffectiveFutureVersionOptions;
    ISysDateEffectivePastVersionOptions     formSysDateEffectivePastVersionOptions;

    ret = super();

    if (ret && !custBillingCodeVersion.AllowChangeAmount)
    {
        select  firstonly custBillingCodeRate
                 where custBillingCodeRate.CustBillingCodeVersion == custBillingCodeVersion.RecId && custBillingCodeRate.Amount == 0 ;

        if (custBillingCodeRate)
        {
            warning(strFmt("@SPS52", custBillingCodeVersion_AllowChangeAmount.labelText(), pageRate.labelText()));
        }
    }

    if (ret
        && custBillingCodeVersion.RecId != 0
        && CustbillingCodeRate::findByCustBillingCodeVersionCurrency(custBillingCodeVersion.RecId, CompanyInfoHelper::standardCurrency()).RecId == 0)
    {
        ret = false;
        warning(strFmt("@SPS45",CompanyInfoHelper::standardCurrency()));
    }

    if (!billingCodeTable)
    {
        localCustBillingCode = CustBillingCode::findBillingCode(billingCodeTable.BillingCode);

        sysDateEffectiveDataManager = SysDateEffectiveDataManager::construct(SysDateEffectiveSetupType::CustBillingCode);

        if (ret && localCustBillingCode.RecId)
        {
            if (!sysDateEffectiveDataManager.existActiveVersion(localCustBillingCode.RecId) &&
                 sysDateEffectiveDataManager.existFutureVersion(localCustBillingCode.RecId))
            {
                args = new Args(formStr(SysDateEffectiveFutureVersionOptions));
                args.caller(element);
                info(strFmt('%1', Global::date2StrUsr(sysDateEffectiveDataManager.getEffectiveDateOfNextFutureVersion(localCustBillingCode.RecId), 1)));
                args.parm(Global::date2StrUsr(sysDateEffectiveDataManager.getEffectiveDateOfNextFutureVersion(localCustBillingCode.RecId), 1));
                formRun = classfactory.formRunClass(args);
                formRun.init();
                formRun.run();
                formRun.wait();

                if (formRun.closedOk())
                {
                    formSysDateEffectiveFutureVersionOptions = formRun as ISysDateEffectiveFutureVersionOptions;
                    if (formHasMethod(formSysDateEffectiveFutureVersionOptions, 'parmModifyExistingRecord'))
                    {
                        modifyExistingRecord = formSysDateEffectiveFutureVersionOptions.parmModifyExistingRecord();
                        if (modifyExistingRecord == NoYes::Yes)
                        {
                            SysDateEffectiveDataManagerBase::activateVersion(sysDateEffectiveDataManager.findFutureVersionByParentId(localCustBillingCode.RecId, true));
                            billingCodeTable_ds.delete();
                            billingCodeTable_ds.executeQuery();
                        }
                    }
                    if (formHasMethod(formSysDateEffectiveFutureVersionOptions, 'parmCreateNewRecord'))
                    {
                        createNewRecord = formSysDateEffectiveFutureVersionOptions.parmCreateNewRecord();
                        if (createNewRecord == NoYes::Yes)
                        {
                            SysDateEffectiveDataManagerBase::createVersionBeforeFutureVersion(
                            sysDateEffectiveDataManager.findFutureVersionByParentId(localCustBillingCode.RecId, true),
                                                                          sysDateEffectiveDataManager.getEffectiveDateOfNextFutureVersion(localCustBillingCode.RecId) - 1);
                            billingCodeTable_ds.delete();
                            billingCodeTable_ds.executeQuery();
                        }
                    }
                }
                ret = false;
            }
            else if ( !sysDateEffectiveDataManager.existActiveVersion(localCustBillingCode.RecId) &&
                       sysDateEffectiveDataManager.existPastVersion(localCustBillingCode.RecId))
            {
                args = new Args(formStr(SysDateEffectivePastVersionOptions));
                args.caller(element);
                args.parm(Global::date2StrUsr(sysDateEffectiveDataManager.getExpirationDateOfLastExpiredVersion(localCustBillingCode.RecId), DateFlags::None));
                formRun = classfactory.formRunClass(args);
                formRun.init();
                formRun.run();
                formRun.wait();

                if (formRun.closedOk())
                {
                    formSysDateEffectivePastVersionOptions = formRun as ISysDateEffectivePastVersionOptions;
                    if (formHasMethod(formSysDateEffectivePastVersionOptions, 'parmActivateExistingRecord'))
                    {
                        activateExistingRecord = formSysDateEffectivePastVersionOptions.parmActivateExistingRecord();
                        if (activateExistingRecord == NoYes::Yes)
                        {
                            SysDateEffectiveDataManagerBase::activateVersion(
                                sysDateEffectiveDataManager.findPastVersionByParentId(localCustBillingCode.RecId, true));
                            billingCodeTable_ds.delete();
                            billingCodeTable_ds.executeQuery();
                        }
                    }
                    if (formHasMethod(formSysDateEffectivePastVersionOptions, 'parmCreateNewRecord'))
                    {
                        createNewRecord = formSysDateEffectivePastVersionOptions.parmCreateNewRecord();
                        if (createNewRecord == NoYes::Yes)
                        {
                            sysDateEffectiveDataManager.createVersionAfterPastVersion(
                                sysDateEffectiveDataManager.findPastVersionByParentId(localCustBillingCode.RecId, true),
                                    sysDateEffectiveDataManager.getExpirationDateOfLastExpiredVersion(localCustBillingCode.RecId) + 1);
                            billingCodeTable_ds.delete();
                            billingCodeTable_ds.executeQuery();
                        }
                    }
                }
                ret = false;
            }
        }
    }

    ret = ret && super();

    return ret;
}

