/// <summary>
/// Handles accessibility of action buttons and controls.
/// </summary>
public void deleted()
{
    int64 recordCount;

    super();
    recordCount = CustBillingCodeLedgerAccount::countRecords( custBillingCodeVersion.RecId );
    if (!found && (recordCount == 0))
    {
        custBillingCodeVersion.AllowChangeLedgerAccount = NoYes::Yes;
        custBillingCodeVersion_ds.refresh();
    }

    if (recordCount == 0)
    {
        btnRemoveLedgerAcc.enabled(false);
    }
}

/// <summary>
/// Initializes a <c>CustBillingCodeLedgerAccount</c> record at creation.
/// </summary>
/// <remarks>
/// The percentage of each new ledger account will be set to a value such that the sum of the percentages of all ledger accounts associated with a billing code is 100.
/// If the existing sum equals or exceeds 100, the new percentage is set to 0.
/// </remarks>
public void initValue()
{
    NonNegPercent   percSum;
    ;

    super();

    percSum = CustBillingCodeLedgerAccount::sumPercent( billingCodeTable.RecId );

    if ( percSum < 100 )
    {
        billingCodeLedgerAccount.Percentage = 100 - percSum;
    }
    else
    {
        billingCodeLedgerAccount.Percentage = 0.00;
    }
}

public void executeQuery()
{
    RecId custBillingLedgerAccountRecId = billingCodeLedgerAccount.RecId;
    RecId custBillingCodeVersionRecId = billingCodeLedgerAccount.CustBillingCodeVersion;
    CustBillingCodeVersion tempCustBillingCodeVersion = CustBillingCodeVersion::find(custBillingCodeVersionRecId);
    super();

    if (!tempCustBillingCodeVersion.validateLedgerAccounts() && custBillingLedgerAccountRecId && custBillingLedgerAccountRecId != billingCodeLedgerAccount.RecId)
    {
        billingCodeTable_ds.research(true);
    }
    element.displayVersionStatus();
}

