display str displayVersionStatus(CustBillingCodeVersion _custBillingCodeVersion)
{
    str expirationStatus;

    if (_custBillingCodeVersion.ValidTo < DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
    {
        expirationStatus = '@AccountsReceivable:CustInterestExpirationStatusExpired';
    }
    else if (_custBillingCodeVersion.ValidFrom > DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
    {
        expirationStatus = '@AccountsReceivable:CustInterestExpirationStatusFuture';
    }
    else
    {
        expirationStatus = '@AccountsReceivable:CustInterestExpirationStatusActive';
    }

    return expirationStatus;
}

