public void dialogClosed(xFormRun _formRun)
{
    ValidFromDate               effectiveDate;
    ValidToDate                 expirationDate;
    RefRecId                    oldCustBillingCodeVersionId;
    CustBillingCodeVersion      custBillingCodeVersionLocal;
    CustBillingCode             oldCustBillingCode;
    ISysDateEffectiveCreateVersion  formSysDateEffectiveCreateVersion;

    super(_formRun);

    if (_formRun.closedOk())
    {
        formSysDateEffectiveCreateVersion = _formRun as ISysDateEffectiveCreateVersion;
        if (formHasMethod(formSysDateEffectiveCreateVersion, 'getEffectiveDate'))
        {
            effectiveDate = formSysDateEffectiveCreateVersion.getEffectiveDate();
            if (!effectiveDate)
            {
                effectiveDate = maxDate();
            }
        }

        if (formHasMethod(formSysDateEffectiveCreateVersion, 'getExpirationDate'))
        {
            expirationDate = formSysDateEffectiveCreateVersion.getExpirationDate();
            if (!expirationDate)
            {
                expirationDate = maxDate();
            }
        }

        oldCustBillingCode = billingCodeTable.data();
        oldCustBillingCodeVersionId = custBillingCodeVersion.RecId;

        ttsbegin;
        custBillingCodeVersionLocal.data(custBillingCodeVersion);
        custBillingCodeVersionLocal.ValidFrom = effectiveDate;
        custBillingCodeVersionLocal.ValidTo = expirationDate;
        custBillingCodeVersionLocal.CustBillingCode = billingCodeTable.RecId;
        custBillingCodeVersionLocal.insert();

        sysDateEffectiveDataManager = SysDateEffectiveDataManager::construct(SysDateEffectiveSetupType::CustBillingCode);
        sysDateEffectiveDataManager.copy(oldCustBillingCodeVersionId, custBillingCodeVersionLocal.RecId);
        ttscommit;

        billingCodeTable_ds.executeQuery();
        element.args().lookupRecord(oldCustBillingCode);
        billingCodeTable_ds.research(false);
    }
}

public void dialogClosed(xFormRun _formRun)
{
    super(_formRun);

    if (_formRun.closed())
    {
        billingCodeTable_ds.executeQuery();
        custBillingCodeCustomField_ds.executeQuery();
    }
}

void clicked()
{
    DialogButton dlgButton;

    if (custBillingCodeVersion.validateLedgerAccounts())
    {
        dlgButton = Box::okCancel("@SYS302501", DialogButton::Ok);

        if (dlgButton == DialogButton::Ok)
        {
            SysDateEffectiveDataManagerBase::activateVersion(custBillingCodeVersion);
            custBillingCodeVersion_ds.research(true);
            billingCodeTable_ds.research(true);
        }
    }
}

/// <summary>
/// Intercepts the change tab action.
/// </summary>
/// <param name="_fromTab">
/// Old tab index.
/// </param>
/// <param name="_toTab">
/// New tab index.
/// </param>
/// <remarks>
/// Prevents user from switching to the Rate tab if the billing code record was not saved.
/// </remarks>

public void tabChanged(int _fromTab, int _toTab)
{
    super(_fromTab, _toTab);

    if (_toTab == #tabLedger || _toTab == #tabRate || _toTab == #tabCustomField)
    {
        //there are zero BillingCodeTable record.
        if (billingCodeTable_ds.getPosition() == -1 )
        {
            billingCodeLedgerAccount_ds.allowCreate(false);
            billingCodeRate_ds.allowCreate(false);
            return;
        }
        else if (billingCodeTable.RecId == 0)
        {
            if (billingCodeTable_ds.validateWrite() == false)
            {
                billingCodeLedgerAccount_ds.allowCreate(false);
                billingCodeRate_ds.allowCreate(false);
                this.tab(#tabGeneral);
                return;
            }
            billingCodeTable_ds.written();
            billingCodeLedgerAccount_ds.allowCreate(true);
            billingCodeRate_ds.allowCreate(true);
        }
        else
        {
            billingCodeLedgerAccount_ds.allowCreate(true);
            billingCodeRate_ds.allowCreate(true);
        }
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.setupInterestOption();

    return ret;
}

/// <summary>
/// Checks to see whether PageLedger tab can be deactivated.
/// </summary>
/// <returns>
/// true if the validations allows the tab to deactivate; otherwise, false.
/// </returns>
/// <remarks>
/// This method prevents the user from deactivating the PageLedger tab if the validation fails.
/// </remarks>

public boolean allowPageDeactivate()
{
    boolean allowDeactivate;
    ;

    allowDeactivate = super();

    if ( allowDeactivate )
    {
        allowDeactivate = custBillingCodeVersion.validateLedgerAccounts();
    }

    if ( !allowDeactivate )
    {
        tab.tab( #tabLedger );
    }

    return allowDeactivate;
}

