[Form]
public class CustChangeProposal extends FormRun
{
    CustTable callerRecord;


}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    if (!this.args() || !this.args().record() || this.args().record().TableId != tableNum(CustTable))
    {
        throw error(strFmt("@SYS22678",element.name()));
    }
    else
    {
        callerRecord = this.args().record() as CustTable;
    }

    if (!callerRecord.canModifyChangeProposal())
    {
        element.disableDiscardButtons();
        // called from an attempted change, and we'll inform the user of the change, 
        // but have to note it can't be done in the current workflow state
        if (this.args().parm())
        {
            warning("@AccountsReceivable:CustTableChangeProposal_ChangesNotAllowed");
            this.args().parm(''); // suppress message since it is not valid if an error occurred
        }
    }

    if (this.args().parm())
    {
        info(this.args().parm());
    }

}

/// <summary>
/// Disables all the discard buttons on the form.
/// </summary>
protected void disableDiscardButtons()
{
    DiscardChanges.enabled(false);
    DiscardCreditMaxChange.enabled(false);
    DiscardTax.enabled(false);
    DiscardName.enabled(false);
    DiscardCustGroup.enabled(false);
    DiscardPaymMode.enabled(false);
    DiscardPaymSpec.enabled(false);
    DiscardVATNum.enabled(false);
    DiscardBankAccount.enabled(false);
    DiscardCashDisc.enabled(false);
    DiscardCreditRating.enabled(false);
    DiscardPaymTermId.enabled(false);
    DiscardMandatoryCreditLimit.enabled(false);
    DiscardCashDiscBaseDays.enabled(false);
    DiscardCustExcludeInterestCharges.enabled(false);
    DiscardCustExcludeCollectionFee.enabled(false);
    DiscardInvoiceAccount.enabled(false);
}

/// <summary>
/// Controls the visibility of groups representing individual grid rows
/// </summary>
public void showHideGroups()
{
    CreditMaxGroup.visible(CustTableChangeProposal.IsChangedCreditMax == NoYes::Yes);
    NameGroup.visible(CustTableChangeProposal.IsChangedName == NoYes::Yes);
    TaxGroupGroup.visible(CustTableChangeProposal.IsChangedTaxGroup == NoYes::Yes);
    CustTableChangeProposal_UpdateEstimatesWhenCreditMaxChanged.visible(CustTableChangeProposal.IsChangedCreditMax == NoYes::Yes);
    CustGroupGroup.visible(CustTableChangeProposal.IsChangedCustGroupId);
    PaymModeGroup.visible(CustTableChangeProposal.IsChangedPaymMode);
    PaymSpecGroup.visible(CustTableChangeProposal.IsChangedPaymSpec);
    VATNumGroup.visible(CustTableChangeProposal.IsChangedVATNum);
    BankAccountGroup.visible(CustTableChangeProposal.IsChangedBankAccountId);
    CashDiscGroup.visible(CustTableChangeProposal.IsChangedCashDiscCode);
    CreditRatingGroup.visible(CustTableChangeProposal.IsChangedCreditRating);
    PaymtermIdGroup.visible(CustTableChangeProposal.IsChangedCustPaymTermId);
    MandatoryCreditLimitGroup.visible(CustTableChangeProposal.IsChangedMandatoryCreditLimit);
    CashDiscBaseDaysGroup.visible(CustTableChangeProposal.IsChangedCashDiscBaseDays);
    CustExcludeInterestChargesGroup.visible(CustTableChangeProposal.IsChangedCustExcludeInterestCharges);
    CustExcludeCollectionFeeGroup.visible(CustTableChangeProposal.IsChangedCustExcludeCollectionFee);
    InvoiceAccountGroup.visible(CustTableChangeProposal.IsChangedInvoiceAccount);
}

/// <summary>
/// Refreshes the calling form for the selected record, forcing it to show/hide "proposal" button based on newest data.
/// </summary>
private void refreshCaller()
{
    element.args().record().dataSource().reread(); // reads from DB
    element.args().record().dataSource().refresh(); // updates form controls
    element.args().record().dataSource().active(); // emulates selection of a record, so that button will be shown/hidden correctly
}

/// <summary>
/// Work that needs to be executed when the form closes, including refreshing the caller.
/// </summary>
public void close()
{
    super();
    element.refreshCaller();
}

/// <summary>
/// Filter to caller and execute query.
/// </summary>
public void executeQuery()
{
    SysQuery::findOrCreateRange(
        this.query().dataSourceTable(tableNum(CustTableChangeProposal)),
        fieldNum(CustTableChangeProposal, CustTable))
    .value(queryValue(callerRecord.RecId));

    super();

    element.showHideGroups();
}

/// <summary>
/// Mark the change as discarded and refresh the form.
/// </summary>
public void clicked()
{
    ttsbegin;
    CustTableChangeProposal localRecord = CustTableChangeProposal::findByCustRecId(CustTableChangeProposal.CustTable, true);
    localRecord.IsChangedCreditMax = NoYes::No;
    localRecord.update();
    ttscommit;

    // Super will ensure the form reflects changes and controls are hidden
    super();
}

