/// <summary>
/// Remove changes and close the dialog.
/// </summary>
public void clicked()
{
    CustTableChangeProposal proposalTable;

    delete_from proposalTable where proposalTable.RecId == CustTableChangeProposal.RecId;

    super();
}

/// <summary>
/// Closes the form.
/// </summary>
public void clicked()
{
    CustTableChangeProposal.deleteIfRedundant();
    super();
}
