Method.Source:
[Form]
public class CustCollectionLetterJournal extends FormRun
{
    Map sumAmountCache;
    CustParameters custParameters;
}

void init()
{
    custParameters = CustParameters::find();

    super();

    if (element.args().record())
    {
        accountNum.visible(false);
    }

    sumAmountCache = new Map(Types::Container, Types::Container);

    CustBillingClassification.visible(CustParameters::isBillingClassificationEnabled());
    Identification_CustBillingClassification.visible(CustParameters::isBillingClassificationEnabled());

    CollectionAgencyGroup_W.visible(custParameters.EnableReferToCollectionAgency_W);
    CustCollectionLetterJour_ds.object(fieldNum(CustCollectionLetterJour, ReferToCollectionAgency_W)).visible(custParameters.EnableReferToCollectionAgency_W);
}

public void insertIntoCache(CustCollectionLetterJour _custCollectionLetterJour)
{
    container collectionAgencyDataCon;

    if (custParameters.EnableReferToCollectionAgency_W)
    {
        collectionAgencyDataCon = _custCollectionLetterJour.collectionAgencyDataCon_W();
        sumAmountCache.insert([_custCollectionLetterJour.CollectionLetterNum, _custCollectionLetterJour.AccountNum],
                               [_custCollectionLetterJour.sumAmount(),
                                conPeek(collectionAgencyDataCon,1),
                                conPeek(collectionAgencyDataCon,2),
                                conPeek(collectionAgencyDataCon,3)]);
    }
    else
    {
        sumAmountCache.insert([_custCollectionLetterJour.CollectionLetterNum, _custCollectionLetterJour.AccountNum],
                               [_custCollectionLetterJour.sumAmount()]);
    }
}

public int active()
{
    int ret;

    ret = super();

    CustCollectionLetterJour_ds.object(fieldNum(CustCollectionLetterJour, ApplyCollectionAgencyTaxRate_W)).allowEdit(custCollectionLetterJour.ReferToCollectionAgency_W);
    CustCollectionLetterJour_ds.object(fieldNum(CustCollectionLetterJour, CollectionAgencyTaxRate_W))
        .allowEdit(custCollectionLetterJour.ReferToCollectionAgency_W && custCollectionLetterJour.ApplyCollectionAgencyTaxRate_W);

    if (custCollectionLetterJour.LedgerVoucher)
    {
        LedgerTransactVoucher.enabled(true);
        Transactions.enabled(true);
    }
    else
    {
        LedgerTransactVoucher.enabled(false);
        Transactions.enabled(false);
    }

    return ret;
}

public void init()
{
    super();
    this.cacheAddMethod(tablemethodstr(CustCollectionLetterJour, sumAmount));
    this.cacheAddMethod(tablemethodstr(CustCollectionLetterJour, taxAmountCur));
    allowEditFieldsOnFormDS_W(CustCollectionLetterJour_DS, false);
}

public void cursorNotify(int _event)
{
    CustCollectionLetterJour collectionLetterCache;
    Set setCollectionLetterNum;

    super(_event);

    if (_event == 0 || _event == 1)
    {
        collectionLetterCache = custCollectionLetterJour_DS.getFirst(0, false);
        while (collectionLetterCache)
        {
            if (! sumAmountCache.exists([collectionLetterCache.CollectionLetterNum, collectionLetterCache.AccountNum]))
            {
                if (! setCollectionLetterNum)
                {
                    setCollectionLetterNum = new Set(Types::Container);
                }
                setCollectionLetterNum.add([collectionLetterCache.CollectionLetterNum, collectionLetterCache.AccountNum]);
            }

            collectionLetterCache = custCollectionLetterJour_DS.getNext();
        }

        if (setCollectionLetterNum)
        {
            sumAmountCache = Map::create(CustCollectionLetterJour::sumAmountDisplayCache(setCollectionLetterNum.pack(), sumAmountCache.pack()));
        }
    }
}

public void modified()
{
    super();
    CustCollectionLetterJour_ds.object(fieldNum(CustCollectionLetterJour, CollectionAgencyTaxRate_W))
        .allowEdit(custCollectionLetterJour.ReferToCollectionAgency_W && custCollectionLetterJour.ApplyCollectionAgencyTaxRate_W);
    sumAmountCache.remove([custCollectionLetterJour.CollectionLetterNum, custCollectionLetterJour.AccountNum]);
    element.insertIntoCache(custCollectionLetterJour);
}

public boolean modified()
{
    var isModified = super();

    if (isModified)
    {
        CustCollectionLetterJour::addStatusFilterToFormDatasource(custCollectionLetterJour_ds, this.selection());

        // Ensure the grid filter reflects the same value
        filterStatusGrid.selection(this.selection());
    }

    return isModified;
}

