public void init()
{
    boolean isBillingClassificationEnabled;

    super();

    if (element.args() && element.args().lookupTable() == tableNum(CustCollectionLetterJour) && element.args().lookupField() && element.args().lookupValue())
    {
        if (element.args().lookupField() == fieldNum(CustCollectionLetterJour, Status))
        {
            PrintPostCancel status;
            status = str2Enum(status, element.args().lookupValue());
            switch (status)
            {
                case PrintPostCancel::Canceled:
                    filterStatusGrid.selection(PrintPostCancelFilter::Canceled);
                    break;
                case PrintPostCancel::Created:
                    filterStatusGrid.selection(PrintPostCancelFilter::Created);
                    break;
                case PrintPostCancel::Posted:
                    filterStatusGrid.selection(PrintPostCancelFilter::Posted);
                    break;
                default:
                    filterStatusGrid.selection(PrintPostCancelFilter::All);
                    break;
            }

            filterStatusGrid.modified();
        }
        else if (element.args().lookupField() == fieldNum(CustCollectionLetterJour, JournalPrinted))
        {
            NoYes jourPrinted;
            jourPrinted = str2Enum(jourPrinted, element.args().lookupValue());
            switch (jourPrinted)
            {
                case NoYes::Yes:
                    filterPrintedGrid.selection(PrintedStatusFilter::Printed);
                    break;
                case NoYes::No:
                    filterPrintedGrid.selection(PrintedStatusFilter::NotPrinted);
                    break;
                default:
                    filterPrintedGrid.selection(PrintedStatusFilter::All);
                    break;
            }

            filterPrintedGrid.modified();
        }
    }

    if (element.args() && element.args().record() && element.args().record().TableId == tableNum(CustTable))
    {
        CustTable custTable = element.args().record();
        SysQuery::findOrCreateRange(custCollectionLetterJour_ds.queryBuildDataSource(), fieldNum(CustCollectionLetterJour, AccountNum)).value(SysQuery::value(custTable.AccountNum));
    }

    isBillingClassificationEnabled = CustParameters::isBillingClassificationEnabled();

    Identification_CustBillingClassification.visible(isBillingClassificationEnabled);
    custCollectionLetterJour_ds.object(fieldNum(CustCollectionLetterJour, CustBillingClassification)).visible(isBillingClassificationEnabled);
}

/// <summary>
///  Enables or disables buttons depending on the collection letter journal status information.
/// </summary>
protected void custCollectionLetterJourDataSourceOnActivated()
{
    if (custCollectionLetterJour.Updated ||
        custCollectionLetterJour.Canceled ||
        custCollectionLetterJour.JournalPrinted)
    {
        custCollectionLetterJour_ds.allowEdit(false);
        custCollectionLetterTrans_ds.allowEdit(false);
        logisticsLocationHeader_ds.allowEdit(false);
        logisticsPostalAddressHeader_ds.allowEdit(false);
        DimensionEntryControl.enabled(false);
    }
    else
    {
        custCollectionLetterJour_ds.allowEdit(true);
        custCollectionLetterTrans_ds.allowEdit(true);
        logisticsLocationHeader_ds.allowEdit(true);
        logisticsPostalAddressHeader_ds.allowEdit(true);
        DimensionEntryControl.enabled(true);
    }

    postCollectionLetter.enabled(custCollectionLetterJour.Status == PrintPostCancel::Created);
    cancelCollectionLetter.enabled(custCollectionLetterJour.Status == PrintPostCancel::Created);
    ledgerTransactVoucher.enabled(custCollectionLetterJour.LedgerVoucher != '');
    custCollectionLetterSendXML.enabled(custCollectionLetterJour.Status == PrintPostCancel::Posted || custCollectionLetterJour.JournalPrinted == NoYes::Yes);                                          
}

public void init()
{
    super();

    // default filter fields in filter pane.
    custCollectionLetterJour_ds.query().dataSourceNo(1).addRange(fieldnum(CustCollectionLetterJour, Status));
    custCollectionLetterJour_ds.query().dataSourceNo(1).addRange(fieldnum(CustCollectionLetterJour, JournalPrinted));

    if (element.args() && element.args().lookupTable() == tableNum(CustCollectionLetterJour) && element.args().lookupField() && element.args().lookupValue())
    {
        NoYes jourPrinted;
        PrintPostCancel status;

        switch (element.args().lookupField())
        {
            case fieldNum(CustCollectionLetterJour, Status):
                status = str2Enum(status, element.args().lookupValue());
                SysQuery::findOrCreateRange(this.queryBuildDataSource(), element.args().lookupField()).value(SysQuery::value(status));
                break;

            case fieldNum(CustCollectionLetterJour, JournalPrinted):
                jourPrinted = str2Enum(jourPrinted, element.args().lookupValue());
                SysQuery::findOrCreateRange(this.queryBuildDataSource(), element.args().lookupField()).value(SysQuery::value(jourPrinted));
                break;
        }
    }

    this.cacheAddMethod(tableMethodStr(CustCollectionLetterJour, sumAmount));
    this.cacheAddMethod(tableMethodStr(CustCollectionLetterJour, taxAmountCur));
}

int active()
{
    int ret = super();

    element.custCollectionLetterJourDataSourceOnActivated();

    return ret;
}

public void modified()
{
    super();

    custCollectionLetterTrans_ds.write();
    CustCollectionLetterJour::updateCollectionLetterCode(custCollectionLetterJour.CollectionLetterNum,custCollectionLetterJour.AccountNum);
    custCollectionLetterJour_ds.reread();
}

public void write(boolean _flag = false)
{
    if (_flag)
    {
        super();
    }
    else
    {
        logisticsPostalAddressHeader_ds.write();
    }

    logisticsLocationHeader_ds.forceWrite(false);
}

public boolean validate()
{
    boolean ret;
    LogisticsLocation location;
    ret = super();
    if (ret)
    {
        if (!logisticsLocationHeader.RecId ||
            logisticsLocationHeader.LocationId != LogisticsLocation::find(logisticsLocationHeader.RecId).LocationId
           )
        {
            location = LogisticsLocation::findByLocationId(logisticsLocationHeader.LocationId);
            if (location)
            {
                // Cannot use existing location.
                ret = checkFailed("@SYS136233");
            }
        }
    }

    return ret;
}

public void modified()
{
    LogisticsLocation location;
    LogisticsPostalAddress postalAddress;

    if (!logisticsLocationHeader.RecId ||
        logisticsLocationHeader.LocationId != LogisticsLocation::find(logisticsLocationHeader.RecId).LocationId
       )
    {
        // Do you want to add a new location?
        if (!logisticsLocationHeader.LocationId || !custCollectionLetterJour.PostalAddress || Box::yesNo("@SYS136234",DialogButton::Ok) == DialogButton::Yes)
        {
            location.LocationId = logisticsLocationHeader.LocationId;
            location.Description = logisticsLocationHeader.Description;
            logisticsLocationHeader.data(location);
            super();
            logisticsPostalAddressHeader.data(postalAddress);
        }
        else
        {
            super();
        }
    }
    else
    {
        super();
    }
}

public void write()
{
    ttsbegin;
    logisticsLocationHeader_ds.write(true);

    logisticsPostalAddressHeader.Location = logisticsLocationHeader.RecId;
    logisticsPostalAddressHeader.validTimeStateUpdateMode(ValidTimeStateUpdate::Correction);
    if (!logisticsPostalAddressHeader.RecId)
    {
        logisticsPostalAddressHeader_ds.write();
    }
    custCollectionLetterJour.PostalAddress = LogisticsPostalAddress::findByLocation(logisticsLocationHeader.RecId).RecId;

    super();
    custCollectionLetterJour_ds.write();
    ttscommit;
}

void clicked()
{
    CustCollectionLetterJour saveCustCollectionLetterJour = custCollectionLetterJour.data();

    super();

    element.args().lookupRecord(saveCustCollectionLetterJour);
    custCollectionLetterJour_ds.research(true);
}

public boolean modified()
{
    var isModified = super();

    if (isModified)
    {
        CustCollectionLetterJour::addStatusFilterToFormDatasource(custCollectionLetterJour_ds, this.selection());

        // Ensure the grid filter reflects the same value
        filterStatusGrid.selection(this.selection());
    }

    return isModified;
}

