/// <summary>
/// Returns a list of marked records from given DS name
/// </summary>
/// <param name = "_name">The name of the datasource to return list marked records</param>
/// <returns>List of marked records</returns>
public List parmListOfMarkedRecordsFromDS(str _name)
{
    return element.getRecIdList(element.parmDataSource(_name));
}

public void showAll()
{
    FormButtonControl button;

    switch (focusItem)
    {
        case CustCollectionsFocusItem::Activities:
            button = ActivitiesFilterButton;
            break;
        case CustCollectionsFocusItem::Cases:
            button = CaseDetailsFilterButton;
            break;
        case CustCollectionsFocusItem::Transactions:
            button = TransactionsFilterButton;
            break;
        default:
            return;
    }

    element.applyFilter(button, CustCollectionsFocusItem::None);
}

private void applyFilter(FormButtonControl _button, CustCollectionsFocusItem _focusItem)
{
    if (_focusItem == CustCollectionsFocusItem::None)
    {
        _button.text("@AccountsReceivable:CustCollectionsShowLinked");
        focusItem = CustCollectionsFocusItem::None;
    }
    else
    {
        focusItem = _focusItem;
        switch (_button)
        {
            case ActivitiesFilterButton:
                _button.text("@AccountsReceivable:CustCollectionsShowAll");
                CaseDetailsFilterButton.text("@AccountsReceivable:CustCollectionsShowLinked");
                TransactionsFilterButton.text("@AccountsReceivable:CustCollectionsShowLinked");
                break;
            case CaseDetailsFilterButton:
                _button.text("@AccountsReceivable:CustCollectionsShowAll");
                ActivitiesFilterButton.text("@AccountsReceivable:CustCollectionsShowLinked");
                TransactionsFilterButton.text("@AccountsReceivable:CustCollectionsShowLinked");
                break;
            case TransactionsFilterButton:
                _button.text("@AccountsReceivable:CustCollectionsShowAll");
                ActivitiesFilterButton.text("@AccountsReceivable:CustCollectionsShowLinked");
                CaseDetailsFilterButton.text("@AccountsReceivable:CustCollectionsShowLinked");
                break;
        }
    }

    element.setFocusQueryValue();
}

private void enableDisableLinkButtons(boolean _initFiltersButton = false)
{
    int numOfMarkedDS = CustTableForActivities_DS.anyMarked() + CustTableForCaseDetail_DS.anyMarked() + CustTableForCustTrans_DS.anyMarked();

    LinkRecords.enabled(numOfMarkedDS > 1);
    UnlinkRecords.enabled(numOfMarkedDS > 1);
    ShowAll.enabled(focusItem != CustCollectionsFocusItem::None);
    CaseDetailsFilterButton.enabled(CustTableForCaseDetail_DS.numberOfRowsLoaded() > 0 || _initFiltersButton);
    TransactionsFilterButton.enabled(CustTableForCustTrans_DS.numberOfRowsLoaded() > 0 || _initFiltersButton);
    ActivitiesFilterButton.enabled(CustTableForActivities_DS.numberOfRowsLoaded() > 0 || _initFiltersButton);
}

private void setFocusQueryValue()
{
    switch (focusItem)
    {
        case CustCollectionsFocusItem::Activities:
            selectedActivity = smmActivities.RecId;
            selectedCase = 0;
            selectedTransaction = 0;
            break;
        case CustCollectionsFocusItem::Cases:
            selectedCase = CaseDetailBase.RecId;
            selectedActivity = 0;
            selectedTransaction = 0;
            break;
        case CustCollectionsFocusItem::Transactions:
            selectedTransaction = CustTrans.RecId;
            selectedActivity = 0;
            selectedCase = 0;
            break;
        case CustCollectionsFocusItem::None:
            selectedActivity = 0;
            selectedCase = 0;
            selectedTransaction = 0;
            break;
    }

    // Activities
    CustTableForActivities_DS.query().dataSourceTable(tableNum(CustCollectionsLinkTable)).enabled(selectedTransaction);
    activitiesCase_ds.enabled(selectedCase);
    activitiesForCasesRange.value(queryValue(caseDetailBase.RecId));
    activitiesForTransactionsRange.value(queryValue(custTrans.RecId));
    activitiesRecIdFocus.value(queryValue(smmActivities.RecId));

    // Cases
    CustTableForCaseDetail_DS.query().dataSourceTable(tableNum(CaseAssociation)).enabled(selectedTransaction);
    CustTableForCaseDetail_DS.query().dataSourceTable(tableNum(CustCollectionsLinkTable)).enabled(selectedActivity);
    casesForTransactionsRange.value(queryValue(custTrans.RecId));
    casesForActivitiesRange.value(queryValue(smmActivities.RecId));
    caseRecIdFocus.value(queryValue(caseDetailBase.RecId));

    // CustTrans
    CustTableForCustTrans_DS.query().dataSourceTable(tableNum(CustCollectionsLinkTable)).enabled(selectedActivity);
    CustTableForCustTrans_DS.query().dataSourceTable(tableNum(CaseAssociation)).enabled(selectedCase);
    transactionsForCasesRange.value(queryValue(caseDetailBase.RecId));
    transactionsForActivitiesRange.value(queryValue(smmActivities.RecId));
    custTransRecIdFocus.value(queryValue(custTrans.RecId));

    element.requeryAll();
    element.enableDisableLinkButtons();
}

private List getRecIdList(FormDataSource _datasource, boolean _autoSelection = false)
{
    if (_datasource && !_datasource.anyMarked() && !_autoSelection)
    {
        return null;
    }

    List selectedRecIds = new List(Types::Int64);

    for (Common record = _datasource ? getFirstSelection(_datasource) : null; record; record = _datasource.getNext())
    {
        selectedRecIds.addEnd(record.RecId);
    }

    return selectedRecIds;
}

/// <summary>
/// Builds up CustCollectionsLinkContext based on given context action
/// </summary>
/// <param name = "_action">CustCollectionsLinkContext action to build context parameters</param>
/// <returns>Built CustCollectionsLinkContext based on given action</returns>
public CustCollectionsLinkContext getLinkContext(CustCollectionsLinkAction _action)
{
    CustCollectionsLinkContext context = CustCollectionsLinkContext::construct();
    context.parmAction(_action);
    context.parmDirPartyRecId(CustTable.Party);

    switch (_action)
    {
        case CustCollectionsLinkAction::LinkActivityAndTransaction:
        case CustCollectionsLinkAction::UnlinkActivityAndTransaction:
            context.parmPrimaryList(element.getRecIdList(smmActivities_DS));
            context.parmReferenceList(element.getRecIdList(CustTrans_DS));
            break;
        case CustCollectionsLinkAction::LinkActivitiesToCase:
        case CustCollectionsLinkAction::UnlinkActivitiesFromCase:
            context.parmPrimaryList(element.getRecIdList(CaseDetailBase_DS));
            context.parmReferenceList(element.getRecIdList(smmActivities_DS));
            break;
        case CustCollectionsLinkAction::LinkTransactionsToCase:
        case CustCollectionsLinkAction::UnlinkTransactionsFromCase:
            context.parmPrimaryList(element.getRecIdList(CaseDetailBase_DS));
            context.parmReferenceList(element.getRecIdList(CustTrans_DS));
            break;
        case CustCollectionsLinkAction::CloseCase:
            context.parmPrimaryList(element.getRecIdList(CaseDetailBase_DS, true));
            break;
        default:
            break;
    }
    return context;
}

