}

private void redrawTabs(FormTabPageControl _tabControl, FormGridControl _grid, FormDataSource _ds)
{
    int counter = _ds.numberOfRowsLoaded();

    if (counter > 0)
    {
        counter = counter > 6 ? 4 : (counter < 2 ? 2 : counter);

        _grid.visible(true);
        _grid.visibleRowsMode(AutoMode::Fixed);
        _grid.visibleRows(counter);
        _grid.visibleRowsValue(counter);
        _tabControl.resetUserSetting();
    }
    else
    {
        _grid.visible(false);
    }
}

/// <summary>
/// Init CaseDetailBase query modifications
/// </summary>
/// <param name = "_query">The init CaseDetailBase query</param>
public void initCaseDetailBases(Query _query)
{
    QueryBuildDataSource ds = _query.dataSourceTable(tableNum(CaseDetailBase));
    ds.clearRanges();
    ds.clearLinks();
    ds.relations(false);
    ds.addLink(fieldNum(CaseDetailBase, Party), fieldNum(CustTable, Party));

    // Cases to CustTrans
    ds = _query.dataSourceTable(tableNum(CaseDetailBase)).addDataSource(tableNum(CaseAssociation));
    ds.addLink(fieldNum(CaseDetailBase, RecId), fieldNum(CaseAssociation, CaseRecId));
    ds.joinMode(JoinMode::InnerJoin);
    ds.enabled(false);
    ds.relations(false);
    ds.addRange(fieldNum(CaseAssociation, EntityType)).value(queryValue(CaseEntityType::CustTrans));
    casesForTransactionsRange = SysQuery::findOrCreateRange(ds, fieldNum(CaseAssociation, RefRecId));

    // Cases focus
    ds = _query.dataSourceTable(tableNum(CaseDetailBase));
    caseRecIdFocus = SysQuery::findOrCreateRange(ds, fieldNum(CaseDetailBase, RecId));

    element.initInstanceRelationTypeRange(ds);

    element.initCasesClosedRange(ds);

    // Cases to Activities
    ds = _query.dataSourceTable(tableNum(CaseDetailBase)).addDataSource(tableNum(CustCollectionsLinkTable));
    ds.addLink(fieldnum(CaseDetailBase, RecId), fieldnum(CustCollectionsLinkTable, PrimaryRecId));
    ds.joinMode(JoinMode::ExistsJoin);
    ds.enabled(false);
    casesForActivitiesRange = SysQuery::findOrCreateRange(ds, fieldnum(CustCollectionsLinkTable, RefRecId));

    custForCasesPartyRange = element.linkCustTableOnParty(_query.dataSourceName(CustTableForCaseDetail_DS.name()));
}

