            break;
    }
}

/// <summary>
/// Updates the form when a new <c>CustTable</c> record is selected.
/// </summary>
public void custActive()
{
    boolean isOK = custCollectionsForm.parmSelectedCustAccountNum() && custTable;

    if (!isOK && priorCustAccountNum && CustTable::exist(priorCustAccountNum)) // bad account number but we have a fall back
    {
        custCollectionsForm.parmSelectedCustAccountNum(priorCustAccountNum);
        custTable_ds.executeQuery();
        return;
    }

    actionPane.enabled(isOK);
    transactionsPage.enabled(isOK);
    activitiesPage.enabled(isOK);

    if (!isOK) // bad account number and no fall back
    {
        return;
    }

    if (priorCustAccountNum != custCollectionsForm.parmSelectedCustAccountNum())
    {
        element.setCommissionPersonEmailForCust();
        contactPersonCollections = custTable.getCollectionsContactPerson();
        contactCollectionsPostalAddress = DirParty::primaryPostalAddress(contactPersonCollections.Party);
        element.setSelectedAgingPeriodDef(custCollectionsForm.parmAgingPeriodDefinition());
    }

    if (priorCustAccountNum != custCollectionsForm.parmSelectedCustAccountNum() || priorAgingPeriodDef != custCollectionsForm.parmAgingPeriodDefinition()) // if switching customers or aging period, reselect default values
    {
        QueryBuildRange range = SysQuery::findOrCreateRange(custAging_ds.queryBuildDataSource(), fieldNum(CustAging, StatRepInterval));
        range.status(RangeStatus::Hidden);
        range.value(queryValue(custCollectionsForm.parmAgingPeriodDefinition()));
        custAging_ds.executeQuery();

        allCompanies = element.findSnapshotCompanies(custAging);
        selectedCompanies = allCompanies;
    }

    priorCustAccountNum = custCollectionsForm.parmSelectedCustAccountNum();
    priorAgingPeriodDef = custCollectionsForm.parmAgingPeriodDefinition();

    element.updateAgingIndication();

    eMailContactAction.enabled(false);
    eMailSalespersonAction.enabled(false);

    if (custCollectionsForm.parmFromCaseListPage())
    {
        custCollectionsForm.parmFromCaseListPage(false);
        element.applyFilter(caseDetailsFilterButton, CustCollectionsFocusItem::Cases);
    }
    else if (custCollectionsForm.parmFromCustTrans())
    {
        custCollectionsForm.parmFromCustTrans(false);
        element.applyFilter(transactionsFilterButton, CustCollectionsFocusItem::Transactions);
    }
    else if (custCollectionsForm.parmFromActivities())
    {
        custCollectionsForm.parmFromActivities(false);
        element.applyFilter(activitiesFilterButton, CustCollectionsFocusItem::Activities);
    }
    else
    {
        element.showAll();
    }
}

public void initActivitiesDataSources(Query _query)
{
    // Eliminate node type activities, i.e. we only want to see leaves
    QueryBuildDataSource ds = SysQuery::findOrCreateDataSource(_query, tableNum(HierarchyTreeTable), tableNum(smmActivities));
    ds.addLink(fieldNum(smmActivities, RecId), fieldNum(HierarchyTreeTable, RefRecId));
    ds.joinMode(JoinMode::NoExistsJoin);
    SysQuery::findOrCreateRange(ds, fieldNum(HierarchyTreeTable, ElementNodeType)).value(queryValue(ElementNodeType::Node));

    // Setup ranges for cases on smmActivityParentLinkTable by TableId, RecId and ParentType.
    ds = _query.dataSourceTable(tableNum(smmActivityParentLinkTable));
    ds.addLink(fieldNum(CustTable, RecId), fieldNum(smmActivityParentLinkTable, RefRecId));
    ds.addRange(fieldNum(smmActivityParentLinkTable, ParentType)).value(queryValue(smmActivityParentType::Case));
    activitiesOriginRange = SysQuery::findOrCreateRange(ds, fieldNum(smmActivityParentLinkTable, ParentType));

    // Sort and ranges for smmActivities
    ds = _query.dataSourceTable(tableNum(smmActivities));
    ds.addSortField(fieldNum(smmActivities, StartDateTime), SortOrder::Descending);
    activitiesRecIdFocus = SysQuery::findOrCreateRange(ds, fieldNum(smmActivities, RecId));

    activitiesClosedRange = SysQuery::findOrCreateRange(ds, fieldNum(smmActivities, Closed));
    activitiesClosedRange.value(int2str(false));

    // Join to narrow activities to a case
    activitiesCase_ds = smmActivityParentLinkTable_DS.queryBuildDataSource().addDataSource(tableNum(smmActivityParentLinkTable), 'smmPLT2', true);
    activitiesCase_ds.joinMode(JoinMode::ExistsJoin);
    activitiesCase_ds.addLink(fieldnum(smmActivityParentLinkTable, ActivityNumber), fieldnum(smmActivityParentLinkTable, ActivityNumber));
    activitiesForCasesRange = SysQuery::findOrCreateRange(activitiesCase_ds, fieldnum(smmActivityParentLinkTable, RefRecId));
    SysQuery::findOrCreateRange(activitiesCase_ds, fieldnum(smmActivityParentLinkTable, RefTableId)).value(queryValue(tableNum(CaseDetailBase)));
    SysQuery::findOrCreateRange(activitiesCase_ds, fieldnum(smmActivityParentLinkTable, ParentType)).value(queryValue(smmActivityParentType::Case));
    activitiesCase_ds.enabled(custCollectionsForm.parmFromCaseListPage());

    // Hide template activities
    SysQuery::findOrCreateRange(ds, fieldNum(smmActivities, IsTemplate)).value(queryValue(NoYes::No));

    // Hide activities private to another user
    SysQuery::findOrCreateRange(ds, fieldNum(smmActivities, ResponsibleWorker)).value(strFmt('((%1 == "%2") || (%3 != %4))',
                fieldStr(smmActivities, ResponsibleWorker), smmUtility::getCurrentContactWorker(),
                fieldStr(smmActivities, Sensitivity), enum2int(smmSensitivity::Privat)));

    custForActivitiesPartyRange = element.linkCustTableOnParty(_query.dataSourceName(CustTableForActivities_DS.name()));

    // Link table
    ds = _query.dataSourceTable(tableNum(smmActivities)).addDataSource(tableNum(CustCollectionsLinkTable));
    ds.addLink(fieldnum(smmActivities, RecId), fieldnum(CustCollectionsLinkTable, PrimaryRecId));
    ds.joinMode(JoinMode::ExistsJoin);
    ds.enabled(false);
    activitiesForTransactionsRange = SysQuery::findOrCreateRange(ds, fieldnum(CustCollectionsLinkTable, RefRecId));
}

public void applyActivitiesDataSources(Query _query)
