    custForActivitiesPartyRange.value(queryValue(CustTable.Party));

    element.setQueryCompanies(_query);
}

public void initCustTransDataSources(Query _query)
{
    // Sorts and ranges for CustTrans
    QueryBuildDataSource ds = _query.dataSourceTable(tableNum(CustTrans));
    ds.addSortField(fieldNum(CustTrans, TransDate));
    custTransClosedRange = SysQuery::findOrCreateRange(ds, fieldNum(CustTrans, Closed));
    custTransRecIdFocus = SysQuery::findOrCreateRange(ds, fieldNum(CustTrans, RecId));

    // Add Case Association data source and ranges
    ds = _query.dataSourceTable(tableNum(CustTrans)).addDataSource(tableNum(CaseAssociation));
    ds.addLink(fieldNum(CustTrans, RecId), fieldNum(CaseAssociation, RefRecId));
    ds.joinMode(JoinMode::InnerJoin);
    ds.relations(false);
    ds.enabled(custCollectionsForm.parmFromCaseListPage());

    ds.addRange(fieldNum(CaseAssociation, EntityType)).value(queryValue(CaseEntityType::CustTrans));

    transactionsForCasesRange = SysQuery::findOrCreateRange(ds, fieldNum(CaseAssociation, CaseRecId));
    custForTransPartyRange = element.linkCustTableOnParty(_query.dataSourceName(CustTableForCustTrans_DS.name()));

    // Link table
    ds = _query.dataSourceTable(tablenum(CustTrans)).addDataSource(tablenum(CustCollectionsLinkTable));
    ds.addLink(fieldNum(CustTrans, RecId), fieldNum(CustCollectionsLinkTable, RefRecId));
    ds.joinMode(JoinMode::ExistsJoin);
    ds.enabled(false);
    transactionsForActivitiesRange = SysQuery::findOrCreateRange(ds, fieldNum(CustCollectionsLinkTable, PrimaryRecId));
}

