public container findSnapshotCompanies(CustAging _custAging)
{
    container companies;
    CompanyInfo companyInfo;
    CustAgingLegalEntity custAgingLegalEntity;

    while select DataArea
        from companyInfo
        exists join custAgingLegalEntity
            where
                custAgingLegalEntity.SourceLegalEntity == companyInfo.RecId &&
                custAgingLegalEntity.CustAging == _custAging.RecId
    {
        companies += companyInfo.DataArea;
    }
    return companies ? companies : [curext()]; // return companies or current company if none was found
}

