public Email getContactEmail(ContactPerson _contact)
{
    return element.getContactLocator(_contact, LogisticsElectronicAddressMethodType::Email);
}

public LogisticsElectronicAddressLocator getContactLocator(ContactPerson _contact, LogisticsElectronicAddressMethodType _type)
{
    DirPartyContactInfoView info;

    // Should be only one primary
    select firstonly Locator
        from info
        where info.Party == _contact.Party &&
            info.IsPrimary == NoYes::Yes &&
            info.Type == _type;

    // If no primary, take the first
    if (!info)
    {
        select firstonly Locator
            from info
            order by Locator
            where info.Party == _contact.Party &&
                info.Type == _type;
    }

    return info.Locator;
}

/// <summary>
/// Determines the default customer account and manipulates the UI accordingly.
/// </summary>
public void determineDefaultCustAccount()
{
    custCollectionsForm.getDefaultCustAccountNum();

    if (custCollectionsForm.parmIsClosedCustTrans())
    {
        element.setViewTransactionsOption(true, false);
    }

    if (custCollectionsForm.parmIsCaseNotInProcess())
    {
        element.setViewCasesOption(true, false);
    }

    if (custCollectionsForm.parmIsActivityClosed())
    {
        element.setViewActivitiesOption(true, false);
    }

    if (!custCollectionsForm.parmSelectedCustAccountNum())
    {
        custCollectionsForm.firstPoolAccountNum();
    }

    if (custCollectionsForm.parmFromCaseListPage())
    {
        caseDetailBase = custCollectionsForm.parmCaseDetailBase();
    }

    if (custCollectionsForm.parmFromActivities())
    {
        smmActivities = custCollectionsForm.parmActivities();
    }

    if (custCollectionsForm.parmFromCustTrans())
    {
        custTrans = custCollectionsForm.parmCustTrans();
    }
}

public boolean hasCustTrans()
{
    return custTrans.RecId != 0;
}

public void init()
{
    // Get args and setup form class object.
    Args args = element.args();

    if (!args || !args.parmObject())
    {
        throw error("@SYS22539");
    }

    custCollectionsForm = args.parmObject();

    args.copyCallerQuery(CopyCallerQuery::No);

    custCollectionsForm.init();

    super();

    element.initializeFormElements();
}

/// <summary>
/// Initializes form elements.
/// </summary>
protected void initializeFormElements()
{
    element.setViewTransactionsOption(false, false);
    element.setViewActivitiesOption(false, false);
    element.setViewCasesOption(false, false);
    element.setViewActivitiesOrigin(true, false);
    element.setShowCurrencyOption(true, false);
    element.enableDisableLinkButtons(true);

    if (custCollectionsForm.getPoolQuery())
    {
        poolId.visible(true);
        poolId.text(custCollectionsForm.parmPoolId());
    }

    companyCurrency = CompanyInfoHelper::standardCurrency();
    allCompanies = CustVendOpenTransManager::findSharedServiceCompanies(curext());

    element.enableAgingAction();
    element.setSelectedAgingPeriodDef();

    element.determineDefaultCustAccount();

    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true);

    CustTrans_CustBillingClassification.visible(CustParameters::isBillingClassificationEnabled());
}

