public void setCommissionPersonEmailForCust()
{
    CommissionSalesRep salesRep;
    DirPerson dirPerson;

    // Get the customer contact if there is one
    select firstonly RecId from dirPerson
        exists join salesRep
            where
                dirPerson.RecId == salesRep.Person &&
                salesRep.GroupId == custTable.SalesGroup &&
                salesRep.CustomersContact == NoYes::Yes;

    // If there wasn't a customer contact, get the first one
    if (!dirPerson)
    {
        select firstonly RecId from dirPerson
            exists join salesRep
                where
                    dirPerson.RecId == salesRep.Person &&
                    salesRep.GroupId == custTable.SalesGroup;
    }

    LogisticsElectronicAddress electronicAddress = DirParty::primaryElectronicAddress(dirPerson.RecId, LogisticsElectronicAddressMethodType::Email);

    if (electronicAddress)
    {
        commissionPersonEmail = electronicAddress.Locator;
    }
    else
    {
        utcdatetime currentDateTime = DateTimeUtil::utcNow();
        DirPartyContactInfoView dirPartyContactInfoView;

        select firstonly Locator from dirPartyContactInfoView
            where dirPartyContactInfoView.Type == LogisticsElectronicAddressMethodType::Email &&
                    dirPartyContactInfoView.IsPrimary == NoYes::No &&
                    dirPartyContactInfoView.ValidFrom <= currentDateTime &&
                    dirPartyContactInfoView.ValidTo >= currentDateTime &&
                    dirPartyContactInfoView.Party == dirPerson.RecId;

        commissionPersonEmail = dirPartyContactInfoView.RecId ? dirPartyContactInfoView.Locator : '';
    }
}

/// <summary>
/// Modifies a query to handle cross company.
/// </summary>
/// <param name="_query">
/// A query.
/// </param>
/// <remarks>
/// Used to support cross-company queries.
/// </remarks>
public void setQueryCompanies(Query _query)
{
    _query.allowCrossCompany(true);
    _query.clearCompanyRange();
    for (int i = 1; i <= conLen(selectedCompanies); i++)
    {
        _query.addCompanyRange(conPeek(selectedCompanies, i));
    }
}

public FormPartControl partByFormName(FormName _formName)
{
    PartList partList = new PartList(element);

    for (int i = 1; i <= partList.partCount(); i++)
    {
        FormPartControl part = partList.getPartControlById(i);

        if (part.formName() == _formName)
        {
            return part;
        }
    }

    return null;
}

