public void updateAgingIndication(boolean _refreshTransGrid = false)
{
    agingInfo = CustVendBalanceList::construct(
            SysModule::Cust,
            custAging.RecId ? custAging.AgedBy : DateTransactionDuedate::DueDate,
            0,
            dateMax(),
            custAging.RecId ? custAging.AgingDate : DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
            NoYes::No,
            DayMonth::Day,
            ForwardBackwardPrinting::Forward,
            custCollectionsForm.parmAgingPeriodDefinition());

    element.populateAgingIndicators();

    if (_refreshTransGrid)
    {
        // Redisplay the selection. Explicitly get/set position since the research(true) method
        // causes flickering effects.
        int currentPosition = custTrans_ds.getPosition();
        custTrans_ds.research(false);
        custTrans_ds.setPosition(currentPosition);
    }
}

public void viewStatementSpreadsheet(DateTransactionDuedate _agedBy, CustAgingDate _agingDate, boolean _includeSettled, TransDate _settledTransStartDate)
{
    CustCollectionsExcelStatement statementGenerator = new CustCollectionsExcelStatement(custTable, custTrans);
    statementGenerator.parmGenerateSummaryStatement(true);
    statementGenerator.parmContactPersonName(contactPersonCollections.personName());
    statementGenerator.parmAgingPeriodDefinition(custCollectionsForm.parmAgingPeriodDefinition());
    statementGenerator.parmAgedBy(_agedBy);
    statementGenerator.parmAgingDate(_agingDate);
    statementGenerator.parmIncludeSettledTransactions(_includeSettled);
    statementGenerator.parmClosedTransDateRangeStart(_settledTransStartDate);
    statementGenerator.parmSelectedCompanies(selectedCompanies);

    using (System.IO.Stream statement = statementGenerator.generateStatement())
    {
        if (statement != null)
        {
            File::SendFileToUser(statement, strFmt('%1%2', strReplace(DateTimeUtil::toStr(DateTimeUtil::utcNow()), ':', ''), '.xlsx'));
        }
    }
}

public CustAgingLinePosition getFirstNonZeroBalanceAgingPeriod(CustTable _custTable)
{
    // Get the last column that has a value
    CustAging customerAging;
    select firstOnly FirstNonzeroBalancePeriod
        from customerAging
        where customerAging.CustTable == _custTable.AccountNum && customerAging.StatRepInterval == custCollectionsForm.parmAgingPeriodDefinition();

    return customerAging ? customerAging.FirstNonzeroBalancePeriod : 0;
}

public void populateAgingIndicators()
{
    int lineNumber = 1;

    custAgingIndicators = new Array(Types::Enum);

    StatRepIntervalLine agingPeriod;
    while select AgingIndicator, LineNum, Name
        from agingPeriod
        order by agingPeriod.LineNum desc
        where agingPeriod.Name == custCollectionsForm.parmAgingPeriodDefinition()
    {
        custAgingIndicators.value(lineNumber, agingPeriod.AgingIndicator);
        lineNumber++;
    }
}

public int task(int _taskId)
{
    int ret;
    #Task

    if (_taskId == #taskSwitchToGridView)
    {
        // Instead of going to the grid on this form, take users to the Collections Pool list page.
        // This is the form that "drives" the collections process, which explains the override.
        new MenuFunction(menuItemDisplayStr(CustCollectionsPoolsListPage), MenuItemType::Display).run();

        ret = 0;
    }
    else if (_taskId == #taskRefresh)
    {
        // Instead of changing customers on refresh, just requery the datasources
        element.requeryAll();
        ret = 0;
    }
    else
    {
        ret = super(_taskId);
    }

    return ret;
}

    /// <summary>
    /// Sets the <c>CustTable</c> record after run.
    /// </summary>
    public void run()
    {
		if (custCollectionsForm.parmSelectedCustAccountNum())
		{
        	SysQuery::findOrCreateRange(CustTable_ds.queryBuildDataSource(), fieldNum(CustTable, AccountNum)).value(SysQuery::value(custCollectionsForm.parmSelectedCustAccountNum()));
		}

        super();

        if (custCollectionsForm.parmFromCaseListPage() || custCollectionsForm.parmFromCustTrans() || custCollectionsForm.parmFromActivities())
        {
            info("@AccountsReceivable:CustCollectionsShowAllOpenMessage");
        }
    }

