public void linkActive()
{
    element.determineDefaultCustAccount();

    // reselect the lookup record to make sure it's a CustTable record regardless of where we come from
    // this also detaches the buffer from the parent form, improving performance of the lookup query
    element.args().lookupRecord(CustTable::find(custCollectionsForm.parmSelectedCustAccountNum()));

    super();
}

public int active()
{
    int ok = super();

    if (custCollectionsForm.parmFromLinkActive()) // If coming from LinkActive, use the selected customer and, optionally, case
    {
        custCollectionsForm.parmFromLinkActive(false);
    }
    else
    {
        custCollectionsForm.parmSelectedCustAccountNum(custTable.AccountNum);
    }

    if (ok)
    {
        element.custActive();
    }

    return ok;
}

[SysClientCacheDataMethod]
public display container showAgingIndicator(CustTable _custTable)
{
    str symbol = '';
    str tooltipStr = '';
    CustAgingLinePosition period = element.getFirstNonZeroBalanceAgingPeriod(_custTable);

    if (1 <= period && period <= custAgingIndicators.lastIndex())
    {
        symbol = StatRepIntervalLine::getResourceIdForAgingIndicator(custAgingIndicators.value(period));
        tooltipStr = enum2Str(custAgingIndicators.value(period));
    }

    return ImageReference::constructForSymbol(symbol, tooltipStr).pack();
}

