[SysClientCacheDataMethod]
public display container showAgingIndicator(CustTrans _custTrans)
{
    str symbol = '';
    date transactionAgeDate;

    if (this.mustUpdateAgingIndicatorBasedOnCustTrans(_custTrans))
    {
        transactionAgeDate = _custTrans.agedByTransactionDueDate(agedBy);

        symbol = StatRepIntervalLine::getResourceIdForAgingIndicator(agingInfo.getAgingIndicatorForDate(transactionAgeDate));
    }

    return ImageReference::constructForSymbol(symbol, enum2Str(agingInfo.getAgingIndicatorForDate(transactionAgeDate))).pack();
}

