public void clicked()
{
    Args args = new Args();
    args.caller(element);
    args.parmEnumType(enumNum(CustCollectionsLinkAction));

    if (CustTrans_DS.anyMarked() && smmActivities_DS.anyMarked())
    {
        args.parmEnum(CustCollectionsLinkAction::LinkActivityAndTransaction);
        CustCollectionsLinkHandler::main(args);
    }

    if (CaseDetailBase_DS.anyMarked() && CustTrans_DS.anyMarked())
    {
        args.parmEnum(CustCollectionsLinkAction::LinkTransactionsToCase);
        CustCollectionsLinkHandler::main(args);
    }

    if (CaseDetailBase_DS.anyMarked() && smmActivities_DS.anyMarked())
    {
        args.parmEnum(CustCollectionsLinkAction::LinkActivitiesToCase);
        CustCollectionsLinkHandler::main(args);
    }
    info("@AccountsReceivable:CustCollectionsRecordsLink");
}

public void clicked()
{
    Args args = new Args();
    args.caller(element);
    args.parmEnumType(enumNum(CustCollectionsLinkAction));

    if (CustTableForCustTrans_DS.anyMarked() && CustTableForActivities_DS.anyMarked())
    {
        args.parmEnum(CustCollectionsLinkAction::UnlinkActivityAndTransaction);
        CustCollectionsLinkHandler::main(args);
    }

    if (CustTableForCaseDetail_DS.anyMarked() && CustTableForCustTrans_DS.anyMarked())
    {
        args.parmEnum(CustCollectionsLinkAction::UnlinkTransactionsFromCase);
        CustCollectionsLinkHandler::main(args);
    }

    if (CustTableForCaseDetail_DS.anyMarked() && CustTableForActivities_DS.anyMarked())
    {
        args.parmEnum(CustCollectionsLinkAction::UnlinkActivitiesFromCase);
        CustCollectionsLinkHandler::main(args);
    }

    element.requeryAll();
    info("@AccountsReceivable:CustCollectionsRecordsUnlink");
}

