    CustCollectionsSettle::settleCustTrans(custTrans, custTable, element);
}

public void clicked()
{
    // Assigning selected transaction to <c>CustTrans</c> data source buffer.
    CustTrans = CustTrans_ds.cursor();

    CustCollectionsFocusItem focus = (focusItem == CustCollectionsFocusItem::Transactions) ? CustCollectionsFocusItem::None : CustCollectionsFocusItem::Transactions;
    element.applyFilter(this, focus);
}

public void clicked()
{
    element.showDocuments(custTrans);
}

public boolean allowPageDeactivate()
{
    boolean allow = super();

    transactionsPage.heightMode(FormHeight::ColumnHeight);

    return allow;
}

void clicked()
{
    super();

    element.refreshActivities();
}

void clicked()
{
    element.setViewActivitiesOption(false);
}

void clicked()
{
    element.setViewActivitiesOption(true);
}

void clicked()
{
    element.setViewActivitiesOrigin(true);
}

void clicked()
{
    element.setViewActivitiesOrigin(false);
}

public void clicked()
{
    CustCollectionsFocusItem focus = (focusItem == CustCollectionsFocusItem::Activities) ? CustCollectionsFocusItem::None : CustCollectionsFocusItem::Activities;
    element.applyFilter(this, focus);
}

