/// <summary>
/// Adds the original query defined on the form to the provided query.
/// </summary>
/// <param name="_poolQuery">
/// The query for the currently selected pool.
/// </param>
/// <returns>
/// The passed query with the <c>smmActivityParentLinkTable</c>, <c>smmActivities</c> and <c>CustTable</c> tables added on.
/// </returns>
public Query addOriginalFormQuery(Query _poolQuery)
{
    Query query = new Query();

    QueryBuildDataSource custTableDs = query.addDataSource(tableNum(CustTable), tableStr(CustTable));
    SysQuery::copyDataSource(_poolQuery.dataSourceTable(tableNum(CustTable)), custTableDs);

    // Add queries to display additional information pertinent to this list page
    QueryBuildDataSource dirPartyPostalAddressViewDs = custTableDs.addDataSource(tableNum(DirPartyPostalAddressView), tableStr(DirPartyPostalAddressView));
    dirPartyPostalAddressViewDs.addLink(fieldNum(CustTable, Party), fieldNum(DirPartyPostalAddressView, Party));
    dirPartyPostalAddressViewDs.joinMode(JoinMode::OuterJoin);

    CustCollectionsListPageHelper::updateDirPartyPostalAddressView(query);

    // Add the CustAging datasource to filter the results and provide a datasource for the Aging Factbox
    QueryBuildDataSource custAgingDs = query.dataSourceTable(tableNum(CustAging));
    if (!custAgingDs)
    {
        custAgingDs = custTableDs.addDataSource(tableNum(CustAging), tableStr(CustAging));
        custAgingDs.addLink(fieldNum(CustTable, AccountNum), fieldNum(CustAging, CustTable));
        custAgingDs.joinMode(JoinMode::ExistsJoin);
    }

    QueryBuildDataSource smmActivityParentLinkTableDs = custTableDs.addDataSource(tableNum(smmActivityParentLinkTable), tableStr(smmActivityParentLinkTable));
    smmActivityParentLinkTableDs.addLink(fieldNum(CustTable, RecId), fieldNum(smmActivityParentLinkTable, RefRecId));
    smmActivityParentLinkTableDs.addLink(fieldNum(CustTable,TableId), fieldNum(smmActivityParentLinkTable, RefTableId));
    smmActivityParentLinkTableDs.joinMode(JoinMode::InnerJoin);
    smmActivityParentLinkTableDs.fetchMode(QueryFetchMode::One2One);
    QueryBuildRange range = SysQuery::findOrCreateRange(smmActivityParentLinkTableDs, fieldNum(smmActivityParentLinkTable, ParentType));
    range.value(queryValue(smmActivityParentType::Collections));
    range.status(RangeStatus::Hidden);

    QueryBuildDataSource smmActivitiesDs = smmActivityParentLinkTableDs.addDataSource(tableNum(smmActivities), tableStr(smmActivities));
    smmActivitiesDs.addLink(fieldNum(smmActivityParentLinkTable, ActivityNumber), fieldNum(smmActivities, ActivityNumber));
    smmActivitiesDs.joinMode(JoinMode::InnerJoin);
    smmActivitiesDs.fetchMode(QueryFetchMode::One2One);
    range = smmActivitiesDs.addRange(fieldNum(smmActivities, Closed));
    range.value(queryValue(NoYes::No));
    range.status(RangeStatus::Hidden);

    return query;
}

public void close()
{
    this.saveLast();

    super();
}

private CustCollectionsPoolId getLast()
{
    container lastValues = xSysLastValue::getValue(curext(), curUserId(), UtilElementType::Form, formStr(CustCollectionsActivitiesListPage), poolFilter.name());

    return conLen(lastValues) ? conPeek(lastValues, 1) : '';
}

public void init()
{
    super();

    listPageHelper = new CustCollectionsListPageHelper(poolFilter);
    listPageHelper.loadPools();

    poolFilter.text(listPageHelper.getInitialFilterValue(this.getLast()));

    validToRun = listPageHelper.checkForAging();

    poolFilter.enabled(listPageHelper.getCurrentAgent() != null);

    showAllActivities = false;

    this.setViewActivitiesOption(false, false);
    currentAgingPeriodDefinition.text(listPageHelper.getDefaultAgingPeriod());
}

/// <summary>
/// Gets the name of the <c>StatRepInterval</c> that the list page is filtering by.
/// </summary>
/// <returns>
/// The <c>CustVendReportName</c> of the correct StatRepInterval.
/// </returns>
public CustVendReportName parmSelectedAgingPeriodDef()
{
    return listPageHelper.getDefaultAgingPeriod();
}

/// <summary>
