[Form]
public class CustCollectionsCaseListPage extends FormRun implements CustCollectionsICallbackRequest
{
    CustCollectionsListPageHelper listPageHelper;
    boolean showOnlyOpenCases;
    CustVendReportName selectedAgingPeriodDef;
    CustCollectionsLinkContext linkContext;

}

/// <summary>
/// Returns a list of marked records from given DS name
/// </summary>
/// <param name = "_name">The name of the datasource to return list marked records</param>
/// <returns>List of marked records</returns>
public List parmListOfMarkedRecordsFromDS(str _name)
{
    return element.getRecIdList(element.parmDataSource(_name));
}

public FormDataSource parmDataSource(str _name)
{
    for (var i = 1; i < element.dataSourceCount(); i++)
    {
        if (element.dataSource(i).name() == _name)
        {
            return element.dataSource(i);
        }
    }
    return null;
}

private List getRecIdList(FormDataSource _datasource)
{
    var selectedRecIds = new List(Types::Int64);

    if (_datasource && !_datasource.anyMarked())
    {
        if (_datasource.cursor())
        {
            selectedRecIds.addEnd(_datasource.cursor().RecId);
            return selectedRecIds;
        }

        return null;
    }

    for (Common record = _datasource ? getFirstSelection(_datasource) : null; record; record = _datasource.getNext())
    {
        selectedRecIds.addEnd(record.RecId);
    }

    return selectedRecIds;
}

public void refreshCases()
{
    custTable_ds.research(true);
}

public void linkActionCompleted(CustCollectionsLinkContext _context)
{
    if (_context.parmAction() == CustCollectionsLinkAction::CloseCase)
    {
        custTable_ds.executeQuery();
    }
}

public void close()
{
    this.saveLast();

    super();
}

public CustCollectionsLinkContext getLinkContext(CustCollectionsLinkAction _linkAction)
{
    Debug::assert(_linkAction == CustCollectionsLinkAction::CloseCase);

    return linkContext;
}

private CustCollectionsPoolId getLast()
{
    container lastValues = xSysLastValue::getValue(curext(), curUserId(), UtilElementType::Form, formStr(CustCollectionsCaseListPage), poolFilter.name());

    return conLen(lastValues) ? conPeek(lastValues, 1) : '';
}

public void init()
{
    super();

    listPageHelper = new CustCollectionsListPageHelper(poolFilter);

    listPageHelper.loadPools();

    poolFilter.text(listPageHelper.getInitialFilterValue(this.getLast()));

    poolFilter.enabled(listPageHelper.getCurrentAgent() != null);

    this.setShowOnlyOpenCases(true, false);

    selectedAgingPeriodDef = listPageHelper.getDefaultAgingPeriod();
    currentAgingPeriodDefinition.text(selectedAgingPeriodDef);
}

/// <summary>
/// Gets the name of the <c>StatRepInterval</c> that the list page is filtering by.
/// </summary>
/// <returns>
/// The <c>CustVendReportName</c> of the correct StatRepInterval.
/// </returns>
public CustVendReportName parmSelectedAgingPeriodDef()
{
    return selectedAgingPeriodDef;
}

/// <summary>
/// Runs the list page.
/// </summary>
/// <remarks>
/// Used to set the focus on the grid so that Ctrl-G filtering works
/// correctly when the page is loaded.
/// </remarks>
public void run()
{
    void setAllowAddYes(FormDataSource _ds, FieldId _fieldId, int arrayindex = 1)
    {
        _ds.object(_fieldId, arrayindex).allowAdd(FormAllowAdd::Yes);
    }

    super();

    setAllowAddYes(custTable_ds, fieldNum(CustTable, CustGroup));
    setAllowAddYes(custTable_ds, fieldNum(CustTable, PaymTermId));
    setAllowAddYes(custTable_ds, fieldNum(CustTable, CashDisc));
    setAllowAddYes(custTable_ds, fieldNum(CustTable, Currency));
    setAllowAddYes(custTable_ds, fieldNum(CustTable, Blocked));
    setAllowAddYes(custTable_ds, fieldNum(CustTable, CreditMax));
    setAllowAddYes(custTable_ds, fieldNum(CustTable, PaymMode));

    setAllowAddYes(dirPartyTable_ds, fieldNum(DirPartyTable, NameAlias));

    setAllowAddYes(dirPartyPostalAddressView_ds, fieldNum(DirPartyPostalAddressView, TimeZone));
    setAllowAddYes(dirPartyPostalAddressView_ds, fieldNum(DirPartyPostalAddressView, ZipCode));
    setAllowAddYes(dirPartyPostalAddressView_ds, fieldNum(DirPartyPostalAddressView, City));
    setAllowAddYes(dirPartyPostalAddressView_ds, fieldNum(DirPartyPostalAddressView, County));
    setAllowAddYes(dirPartyPostalAddressView_ds, fieldNum(DirPartyPostalAddressView, State));
    setAllowAddYes(dirPartyPostalAddressView_ds, fieldNum(DirPartyPostalAddressView, CountryRegionId));

    grid.setFocus();
}

private void saveLast()
{
    xSysLastValue::putValue([poolFilter.valueStr()], curext(), curUserId(), UtilElementType::Form, formStr(CustCollectionsCaseListPage), poolFilter.name());
}

public void setButtonAccess()
{
    boolean gridPopulated = custTable_ds.getFirst() != null;

    newActivitiesActionsGroup.enabled(gridPopulated);
    manage.enabled(gridPopulated);
    journals.enabled(gridPopulated);

    communicateTab.enabled(gridPopulated);
    generalTab.enabled(gridPopulated);

    closeCase.enabled(gridPopulated);

    // Always show the Show Closed button, just in case
    view.enabled(true);

    // Disable the other buttons in the group
    transactions.enabled(gridPopulated);
    cases.enabled(gridPopulated);
    activities.enabled(gridPopulated);

    AddtoOutlookContacts.visible(!smmOutlookSyncDeprecationHandler::isOutlookSyncDisabled());
}

public void setShowOnlyOpenCases(boolean _showOnlyOpenCases, boolean _executeQuery = true)
{
    viewCasesOpenAction.enabled(!_showOnlyOpenCases);
    viewCasesAllAction.enabled(_showOnlyOpenCases);

    showOnlyOpenCases = _showOnlyOpenCases;

    if (_executeQuery)
    {
        custTable_ds.executeQuery();
    }
}

public void showDocuments(Common _record)
{
    if (_record)
    {
        smmUtility::openSmmDocHandling(_record, element);
        // The following is the preferred doc handling but it doesn't work with
        // the Activities grid when the data source is smmActivityParentLinkTable
        // rather than smmActivities. But making smmActivities the data source
        // on the Activities grid does not work due to bug AX6:15453.
        //        DocuRef::openDocHandling(_record, element);
    }
}

public int active()
{
    int active = super();

    closeCase.enabled(custCollectionsCaseDetail.Status != CaseStatus::Closed);

    return active;
}

public void executeQuery()
{
    Query combinedQuery = listPageHelper.getCurrentPoolQuery();
    QueryBuildDataSource custTableDs;

    if (listPageHelper.checkForAging())
    {
        // Use the pool query as the base query and add the necessary data sources for the form
        custTableDs = combinedQuery.dataSourceName(tableStr(CustTable));
        QueryBuildDataSource custCollectionsCaseDetailDs = custTableDs.addDataSource(tableNum(CustCollectionsCaseDetail), tableStr(CustCollectionsCaseDetail));

        custCollectionsCaseDetailDs.addLink(fieldNum(CustTable, Party), fieldNum(CustCollectionsCaseDetail, Party));
        custCollectionsCaseDetailDs.fields().addField(fieldNum(CustCollectionsCaseDetail, Party));
        custCollectionsCaseDetailDs.joinMode(JoinMode::InnerJoin);

        if (showOnlyOpenCases)
        {
            custCollectionsCaseDetailDs.addRange(fieldNum(CustCollectionsCaseDetail, Status)).value(queryValue(CaseStatus::None));
            custCollectionsCaseDetailDs.addRange(fieldNum(CustCollectionsCaseDetail, Status)).value(queryValue(CaseStatus::Planned));
            custCollectionsCaseDetailDs.addRange(fieldNum(CustCollectionsCaseDetail, Status)).value(queryValue(CaseStatus::InProcess));
        }

        SysQuery::findOrCreateRange(combinedQuery.dataSourceTable(tableNum(CustAging)), fieldNum(CustAging, StatRepInterval)).value(selectedAgingPeriodDef);
    }
    else
    {
        // No aging snapshot has been run so add a hidden range that doesn't return any results
        custTableDs = combinedQuery.dataSourceName(tableStr(CustTable));
        QueryBuildRange custAccountRange = custTableDs.addRange(fieldNum(CustTable, AccountNum));
        custAccountRange.value('1==2');
        custAccountRange.status(RangeStatus::Hidden);
    }

    QueryBuildDataSource dirPartyPostalAddressViewDs = custTableDs.addDataSource(tableNum(DirPartyPostalAddressView), tableStr(DirPartyPostalAddressView));
    dirPartyPostalAddressViewDs.addLink(fieldNum(CustTable, Party), fieldNum(DirPartyPostalAddressView, Party));
    dirPartyPostalAddressViewDs.joinMode(JoinMode::OuterJoin);

    CustCollectionsListPageHelper::updateDirPartyPostalAddressView(combinedQuery);

    this.query(combinedQuery);

    if (element.args() && element.args().lookupRecord() && element.args().lookupRecord().TableId == tableNum(CustTable))
    {
        CustTable custTableLocal = element.args().lookupRecord();
        SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(CustTable, AccountNum)).value(SysQuery::value(custTableLocal.AccountNum));
    }

    super();

    element.setButtonAccess();
}

void clicked()
{
    if (custCollectionsCaseDetail)
    {
        Args args = new Args(element);
        args.parmObject(CustCollectionsForm::construct(custCollectionsCaseDetail, poolFilter.text(), selectedAgingPeriodDef));

        this.menufunction().run(args);
    }
}

public void clicked()
{
    List caseList = element.getRecIdList(custCollectionsCaseDetail_DS);

    linkContext = CustCollectionsLinkContext::construct();
    linkContext.parmPrimaryList(caseList);
    linkContext.parmDirPartyRecId(custTable.Party);
    linkContext.parmAction(CustCollectionsLinkAction::CloseCase);

    // The linkContext will be retrieved via the getlinkContext method on
    // the form. This is necessary since the DropDialog must be launched via the super()
    // method in order to get the correct window positioning.
    super();
}

void clicked()
{
    element.setShowOnlyOpenCases(true);
}

void clicked()
{
    element.setShowOnlyOpenCases(false);
}

void clicked()
{
    element.showDocuments(custCollectionsCaseDetail);
}

void clicked()
{
    super();
    smmContactPersonSynchronize::selectPartyFromDataSource(custTable_ds);
}

public void lookup()
{
    listPageHelper.createPoolLookup();
}

public boolean modified()
{
    boolean ret = super();
    selectedAgingPeriodDef = listPageHelper.getDefaultAgingPeriod();
    currentAgingPeriodDefinition.text(selectedAgingPeriodDef);

    custTable_ds.executeQuery();
    grid.setFocus();
    return ret;
}
