[Form]
public class CustCollectionsPoolsListPage extends FormRun
{
    CustCollectionsListPageHelper listPageHelper;
    CustVendReportName selectedCustAging;

    boolean validToRun;
    boolean useInitialQuery;

    #define.CustAgingDsName('CustAging1')
    #define.CustAgingLegalEntityName('CustAgingLegalEntity1')
    #define.DirPartyTableContactDsName('DirPartyTableContact')
    #define.DirPartyTableCustDsName('DirPartyTable')

    Array agingIndicators;
    #ResAppl
