/// <summary>
/// Adds the data sources defined for the default pool query to the provided query.
/// </summary>
/// <param name="_query">
/// The query for the currently selected pool.
/// </param>
/// <returns>
/// The passed query with the aging tables added on.
/// </returns>
public Query addOriginalPoolQuery(Query _query)
{
    QueryBuildDataSource custTableDs;
    QueryBuildDataSource custAgingDs;
    QueryBuildDataSource custAgingLegalEntityDs;
    QueryBuildDataSource originalCustAgingDs;
    QueryBuildDataSource dirPartyPostalAddressViewDs;
    QueryBuildDataSource custCollectionsContactDs;
    QueryBuildDataSource contactPersonDs;
    QueryBuildDataSource dirPartyTableContactDs;
    QueryBuildDataSource dirPartyContactInfoViewDs;
    QueryBuildRange agingRange;
    str selectedCustAgingFilter = SysQueryRangeUtil::literalFilterValue(selectedCustAging);

    custTableDs = _query.dataSourceTable(tableNum(CustTable));

    // At this point we can be sure the CustAging datasource exists on the query since it is part
    // of the default pool criteria. Even if the datasource is "removed" from the query dialog, it
    // remains as a disabled datasource.

    // If the pool does not contain a value for the aging period definition, set it here.
    originalCustAgingDs = _query.dataSourceTable(tableNum(CustAging));
    SysQuery::findOrCreateRange(originalCustAgingDs, fieldNum(CustAging, StatRepInterval)).value(selectedCustAgingFilter);

    // The pool query is all about filtering data so at this point those datasources should
    // not be modified in any way; however, to display information on the form we need the
    // same datasources. Add brand new datasources that match the form's datasource names
    // in order to display the appropriate data.

    custAgingDs = _query.dataSourceName(#CustAgingDsName);
    if (!custAgingDs)
    {
        custAgingDs = custTableDs.addDataSource(tableNum(CustAging), #CustAgingDsName);
        custAgingDs.addLink(fieldNum(CustTable, AccountNum), fieldNum(CustAging, CustTable));
        agingRange = SysQuery::findOrCreateRange(custAgingDs, fieldNum(CustAging, StatRepInterval));
        agingRange.value(selectedCustAgingFilter);
        agingRange.status(RangeStatus::Hidden);
        custAgingDs.joinMode(JoinMode::OuterJoin);

        custAgingLegalEntityDs = custAgingDs.addDataSource(tableNum(CustAgingLegalEntityView), #CustAgingLegalEntityName);
        custAgingLegalEntityDs.addLink(fieldNum(CustAging, RecId), fieldNum(CustAgingLegalEntityView, CustAging), #CustAgingDsName);
        custAgingLegalEntityDs.joinMode(JoinMode::OuterJoin);
    }

    // Add datasources necessary for viewing additional information pertinent to the collections process
    dirPartyPostalAddressViewDs = custTableDs.addDataSource(tableNum(DirPartyPostalAddressView), tableStr(DirPartyPostalAddressView));
    dirPartyPostalAddressViewDs.addLink(fieldNum(CustTable, Party), fieldNum(DirPartyPostalAddressView, Party));
    dirPartyPostalAddressViewDs.joinMode(JoinMode::OuterJoin);

    CustCollectionsListPageHelper::updateDirPartyPostalAddressView(_query);

    custCollectionsContactDs = custTableDs.addDataSource(tableNum(CustCollectionsContact), tableStr(CustCollectionsContact));
    custCollectionsContactDs.addLink(fieldNum(CustTable, AccountNum), fieldNum(CustCollectionsContact, AccountNum));
    custCollectionsContactDs.joinMode(JoinMode::OuterJoin);
    // This range is only present to hide the data source from the query form
    custCollectionsContactDs.addRange(fieldNum(CustCollectionsContact, RecId)).status(RangeStatus::Hidden);

    contactPersonDs = custCollectionsContactDs.addDataSource(tableNum(ContactPerson), tableStr(ContactPerson));
    contactPersonDs.addLink(fieldNum(CustCollectionsContact, ContactPersonId), fieldNum(ContactPerson, ContactPersonId));
    contactPersonDs.joinMode(JoinMode::OuterJoin);
    // This range is only present to hide the data source from the query form
    contactPersonDs.addRange(fieldNum(ContactPerson, RecId)).status(RangeStatus::Hidden);

    dirPartyTableContactDs = contactPersonDs.addDataSource(tableNum(DirPartyTable), #DirPartyTableContactDsName);
    dirPartyTableContactDs.addLink(fieldNum(ContactPerson, Party), fieldNum(DirPartyTable, RecId));
    dirPartyTableContactDs.joinMode(JoinMode::OuterJoin);
    // This range is only present to hide the data source from the query form
    dirPartyTableContactDs.addRange(fieldNum(DirPartyTable, RecId)).status(RangeStatus::Hidden);

    dirPartyContactInfoViewDs = contactPersonDs.addDataSource(tableNum(DirPartyContactInfoView), tableStr(DirPartyContactInfoView));
    dirPartyContactInfoViewDs.addLink(fieldNum(ContactPerson, Party), fieldNum(DirPartyContactInfoView, Party));
    dirPartyContactInfoViewDs.joinMode(JoinMode::OuterJoin);
    // This range is only present to hide the data source from the query form
    dirPartyContactInfoViewDs.addRange(fieldNum(DirPartyContactInfoView, Party)).status(RangeStatus::Hidden);

    CustCollectionsListPageHelper::updateDirPartyContactInfoView(_query);

    QueryBuildDataSource hcmWokerDs = custTableDs.addDataSource(tableNum(HcmWorker), tableStr(HcmWorker));
    hcmWokerDs.addLink(fieldNum(CustTable, MainContactWorker), fieldNum(HcmWorker, RecId));
    hcmWokerDs.joinMode(JoinMode::OuterJoin);

    QueryBuildDataSource dirPersonDs = hcmWokerDs.addDataSource(tableNum(DirPerson), tableStr(DirPerson));
    dirPersonDs.addLink(fieldNum(HcmWorker, Person), fieldNum(DirPerson, RecId));
    dirPersonDs.joinMode(JoinMode::OuterJoin);

    // Sort by customer name
    _query.dataSourceName(#DirPartyTableCustDsName).addOrderByField(fieldNum(DirPartyTable, Name));

    return _query;
}

public void close()
{
    //Save the selected value
    this.saveLast();

    super();
}

public CustAgingLinePosition getFirstNonZeroBalanceAgingPeriod(CustTable _custTable)
{
    CustAging customerAging;
    int agingPeriodPosition;

    // Get the last column that has a value
    select firstOnly FirstNonzeroBalancePeriod
        from customerAging
        where customerAging.CustTable == _custTable.AccountNum
        && customerAging.StatRepInterval == selectedCustAging;

    if (customerAging)
    {
        agingPeriodPosition = customerAging.FirstNonzeroBalancePeriod;
    }
    else
    {
        agingPeriodPosition = 0;
    }

    return agingPeriodPosition;
}

private CustCollectionsPoolId getLast()
{
    str empty;
    container lastValues = xSysLastValue::getValue(curext(), curUserId(), UtilElementType::Form, formStr(CustCollectionsPoolsListPage), poolFilter.name());

    if (conLen(lastValues) > 0)
    {
        return conPeek(lastValues, 1);
    }
    else
    {
        return empty;
    }
}

public void init()
{
    CustCollectionsPoolId poolFormValue, filterValue;
    CustCollectionsPool argsCustCollectionsPool;
    CustCollectionsAgent agent;
    boolean enteredFromForm = false;

    super();

    agingIndicators = new Array(Types::Enum);
    useInitialQuery = element.args().initialQuery() != null;
    useInitialQuery = useInitialQuery && element.args().menuItemName() != menuItemDisplayStr(CustCollectionPoolsListPageCustomersPastCreditLimit);
    useInitialQuery = useInitialQuery && element.args().menuItemName() != menuItemDisplayStr(CustCollectionsAgedBucketsChartClick);

    if (element.args() && element.args().record() && element.args().record().TableId == tableNum(CustCollectionsPool))
    {
        argsCustCollectionsPool = element.args().record();
        poolFormValue = argsCustCollectionsPool.Name;
        enteredFromForm = true;
    }

    if (element.args().menuItemName() == menuItemDisplayStr(CustCollectionsAgedBucketsChartClick) && !element.args().parmObject())
    {
        throw error(Error::missingParameter(element.args().caller()));
    }

    if (element.args().menuItemName() == menuItemDisplayStr(CustCollectionsBIPoolsListPage) && !element.args().parmObject())
    {
        throw error(Error::missingParameter(element.args().caller()));
    }

    listPageHelper = new CustCollectionsListPageHelper(poolFilter, enteredFromForm);
    listPageHelper.loadPools();

    filterValue = listPageHelper.getInitialFilterValue(this.getLast(), poolFormValue);
    poolFilter.text(filterValue);

    validToRun = listPageHelper.checkForAging();

    agent = listPageHelper.getCurrentAgent();
    if (!agent)
    {
        poolFilter.enabled(false);
    }

    selectedCustAging = this.getSelectedAgingPeriod();
    currentAgingPeriodDefinition.text(selectedCustAging);

    if (element.args().menuItemName() == menuItemDisplayStr(CustCollectionsAgedBucketsChartClick))
    {
        var formParameters = element.args().parmObject() as Map;

        poolFilter.text(formParameters.lookup('yValue'));
    }

    this.setPinToWorkspaceVisibility();
}

private void setPinToWorkspaceVisibility()
{
    const FormControlName SystemDefinedPinToWorkspaceButton = 'SystemDefinedPinToWorkspaceButton';

    FormControl pinToWorkspaceButton = this.design().controlName(SystemDefinedPinToWorkspaceButton);
    if (pinToWorkspaceButton && pinToWorkspaceButton.visible())
    {
        boolean calledDirectly = this.args().caller() == null;

        pinToWorkspaceButton.visible(calledDirectly);
    }
}

