/// <summary>
/// Builds a list of the columns in Grid that are aging buckets
/// </summary>
/// <returns><c>Array</c> of controls that represent the aging bucket columns</returns>
public Array getGridAgingBucketColumns()
{
    Array columns = new Array(Types::Class);

    if (CurrencyFilter.selection() == enum2int(LedgerCurrencySelection::Accounting))
    {
        columns.value(1, custAgingLine1_OpenBalanceMst);
        columns.value(2, custAgingLine2_OpenBalanceMst);
        columns.value(3, custAgingLine3_OpenBalanceMst);
        columns.value(4, custAgingLine4_OpenBalanceMst);
        columns.value(5, custAgingLine5_OpenBalanceMst);
        columns.value(6, custAgingLine6_OpenBalanceMst);
    }
    else if (CurrencyFilter.selection() == enum2int(LedgerCurrencySelection::Reporting))
    {
        columns.value(1, CustAgingLine1_OpenBalanceReportingCurrency);
        columns.value(2, CustAgingLine2_OpenBalanceReportingCurrency);
        columns.value(3, CustAgingLine3_OpenBalanceReportingCurrency);
        columns.value(4, CustAgingLine4_OpenBalanceReportingCurrency);
        columns.value(5, CustAgingLine5_OpenBalanceReportingCurrency);
        columns.value(6, CustAgingLine6_OpenBalanceReportingCurrency);
    }
    return columns;
}

/// <summary>
/// Changes the text in the aging period balance columns or hides them as necessary.
/// </summary>
/// <remarks>
/// This method hides columns that are not necessary. The user could
/// display them by adding them back in and those columns would have the previous text
/// in them. The values under them would all be zero.
/// </remarks>
public void setGridColumnLabels()
{
    StatRepIntervalLine intervalLines;
    Array columns = element.getGridAgingBucketColumns();

    int lineNumber = 1;

    if (listPageHelper.checkAgingPeriodValid(selectedCustAging))
    {
        while select Heading from intervalLines
            order by intervalLines.LineNum desc
            where intervalLines.Name == selectedCustAging
        {
            if (strLen(intervalLines.Heading) > 0)
            {
                columns.value(lineNumber).showLabel(true);
                columns.value(lineNumber).label(intervalLines.Heading);
            }
            else
            {
                columns.value(lineNumber).showLabel(false);
            }

            columns.value(lineNumber).visible(true);
            lineNumber++;
        }
    }

    Counter numberOfAgedBuckets = CustVendBalanceList::numOfBalanceAge();
    while (lineNumber < numberOfAgedBuckets)
    {
        columns.value(lineNumber).visible(false);

        lineNumber++;
    }
}

public CustVendReportName getSelectedAgingPeriod()
{
    CustVendReportName agingPeriodName;

    // See if we can get the Aging Period from the passed query
    if (useInitialQuery)
    {
        agingPeriodName = SysQuery::findOrCreateRange(element.args().initialQuery().query().dataSourceTable(tableNum(CustAging)), fieldNum(CustAging, StatRepInterval)).value();
    }

    // no, so use the default
    if (!agingPeriodName)
    {
        useInitialQuery = false;
        agingPeriodName = listPageHelper.getDefaultAgingPeriod();
    }

    return agingPeriodName;
}

