public display container showAgingIndicator(CustTable _custTable)
{
    ImageReference imgRef = ImageReference::constructForSymbol('');

    CustAgingLinePosition nonZeroBalanceAgingPeriod;
    int periodCount = agingIndicators.lastIndex();

    nonZeroBalanceAgingPeriod = element.getFirstNonZeroBalanceAgingPeriod(_custTable);

    if (nonZeroBalanceAgingPeriod
        && nonZeroBalanceAgingPeriod <= periodCount)
    {
        // Set the icon appropriately
        imgRef = ImageReference::constructForSymbol(StatRepIntervalLine::getResourceIdForAgingIndicator(agingIndicators.value(nonZeroBalanceAgingPeriod)),
            enum2Str(agingIndicators.value(nonZeroBalanceAgingPeriod)));
    }

    return imgRef.pack();
}

public display Timezone displayTimezone(CustTable _custTable)
{
    return DirParty::primaryPostalAddress(_custTable.Party).TimeZone;
}

public void executeQuery()
{
    // Use the query from the cue?
    if (!useInitialQuery)
    {
        this.query(element.addOriginalPoolQuery(listPageHelper.getCurrentPoolQuery()));

        if (element.args().menuItemName() == menuItemDisplayStr(CustCollectionPoolsListPageCustomersPastCreditLimit))
        {
            QueryBuildDataSource qbds = this.queryBuildDataSource().addDataSource(tableNum(CustomersOverCreditLimitView));
            qbds.addLink(fieldNum(CustTable, AccountNum), fieldNum(CustomersOverCreditLimitView, AccountNum));
            qbds.joinMode(JoinMode::ExistsJoin);
        }
        else if (element.args().menuItemName() == menuItemDisplayStr(CustCollectionsAgedBucketsChartClick))
        {
            var formParameters = element.args().parmObject() as Map;

            LineNum lineNumToSelect = str2Num(formParameters.lookup('key'));
            CustAccount accountFilter = formParameters.lookup('parameter2');
            selectedCustAging = formParameters.lookup('parameter1');

            this.query(element.addOriginalPoolQuery(listPageHelper.getCurrentPoolQuery()));

            currentAgingPeriodDefinition.text(selectedCustAging);

            if (listPageHelper.checkAgingPeriodValid(selectedCustAging))
            {
                if (accountFilter == '')
                {
                    Array columns = element.getGridAgingBucketColumns();

                    StatRepIntervalLine intervalLines;
                    int lineNumber = 1;

                    // Since the LineNum field does not directly represent the fields in the grid we must loop over
                    // the records how ever the ording is the same
                    while select LineNum from intervalLines
                        order by intervalLines.LineNum desc
                        where intervalLines.Name == selectedCustAging
                    {
                        if (intervalLines.LineNum == lineNumToSelect)
                        {
                            break;
                        }
                        lineNumber++;
                    }

                    FieldId agingFieldToFilter = columns.value(lineNumber).dataField();

                    // Since the current query is an outer join to <c>CustAging</c> an exist join to <c>CustAging</ is added to filter customers
                    // to those who have non zero value in the aging bucket
                    QueryBuildDataSource qbds = this.queryBuildDataSource().addDataSource(tableNum(CustAging));
                    qbds.addRange(agingFieldToFilter).value('>0');
                    qbds.addRange(fieldNum(CustAging, StatRepInterval)).value(SysQuery::value(selectedCustAging));
                    qbds.addLink(fieldNum(CustTable, AccountNum), fieldNum(CustAging, CustTable));
                    qbds.joinMode(JoinMode::ExistsJoin);
                }
                else
                {
                    SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(CustTable, AccountNum)).value(SysQuery::value(accountFilter));
                }
            }
        }
        else if (element.args().menuItemName() == menuItemDisplayStr(CustCollectionsBIPoolsListPage))
        {
            CustCollectionsBIReportsHandler::buildQueryForCollectionsPools(custTable_ds.query(), element.args().parmEnum(), element.args().parmObject() as PBIReportSelectedData);
        }
    }

    if (element.args() && element.args().lookupRecord() && element.args().lookupRecord().TableId == tableNum(CustTable))
    {
        CustTable custTableLocal = element.args().lookupRecord();
        SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(CustTable, AccountNum)).value(SysQuery::value(custTableLocal.AccountNum));
    }

    element.populateAgingIndicators(selectedCustAging);

    super();

    element.setButtonAccess();
    element.setGridControlVisibility();
    element.setGridColumnLabels();
}

void clicked()
{
    Args args = new Args(element);
    args.parmObject(CustCollectionsForm::construct(custTable, poolFilter.text(), CustAging1.StatRepInterval));

    this.menuFunction().run(args);
}

