public void lookup()
{
    listPageHelper.createPoolLookup();
}

public boolean modified()
{
    boolean ret = super();

    // once the user selects a pool, we no longer use the initial (cue) query
    useInitialQuery = false;

    selectedCustAging = element.getSelectedAgingPeriod();
    currentAgingPeriodDefinition.text(selectedCustAging);
    validToRun = listPageHelper.checkForAging();

    custTable_ds.executeQuery();
    grid.setFocus();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    element.setGridControlVisibility();
    element.setGridColumnLabels();
    custTable_ds.refresh();
    grid.setFocus();
    return ret;
}
