public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tablenum(InventDim))
    {
        docCursor = custConfirmTrans;
    }

    return docCursor;
}

void init()
{
    #winApi

    super();

    if (element.args().record())
    {
        custConfirmJour_ds.object(fieldnum(CustConfirmJour, InvoiceAccount)).visible(false);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);
    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        element.setVisible_IN();
    }
    // </GIN>

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TaxJournalTrans.visible(false);
    }
}

public void setVisible_IN()
{
    boolean checkParameter = EximParameters_IN::checkParameters();
    QueryBuildDataSource    dataSourceEximCustConfirmTrans;

    EximCustConfirmTrans_ds.object(fieldNum(EximCustConfirmTrans_IN, EximPorts)).visible(checkParameter);
    EximCustConfirmTrans_ds.object(fieldNum(EximCustConfirmTrans_IN, EximProductGroupTable)).visible(checkParameter);
    EximCustConfirmTrans_ds.object(fieldNum(EximCustConfirmTrans_IN, AssessableValue)).visible(checkParameter);

    if (!checkParameter)
    {
        if (CustConfirmTrans_ds && CustConfirmTrans_ds.query())
        {
            dataSourceEximCustConfirmTrans = CustConfirmTrans_ds.query().dataSourceTable(tablenum(EximCustConfirmTrans_IN));
            if (dataSourceEximCustConfirmTrans)
            {
                dataSourceEximCustConfirmTrans.enabled(false);
            }
        }
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

public void init()
{
    QueryBuildDataSource  queryDataSourceLink;

    super();

    if (element.args().dataset() == tablenum(SalesTable))
    {
        this.query().dataSourceTable(tablenum(CustConfirmJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tablenum(CustConfirmJour)).addDataSource(tablenum(CustConfirmSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldnum(CustConfirmSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldnum(SalesTable, SalesId));
    }

    if (element.args().dataset() == tablenum(SalesQuotationTable))
    {
        this.query().dataSourceTable(tablenum(CustConfirmJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tablenum(CustConfirmJour)).addDataSource(tablenum(CustConfirmSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldnum(CustConfirmSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldnum(SalesQuotationTable, SalesIdRef));
    }
}
