Method.Source:
[Form]
public class CustConsInvoice_JP extends FormRun
{
    boolean researchData;
}

public void init()
{
    if (!CustConsInvoiceType_JP::isCustConsInvoiceEnabled())
    {
        // The 'Use consolidated invoice for customer' parameter is not selected.
        throw error("@GLS63183");
    }

    super();
}

internal void refreshAfterPostAndReversal()
{
    custConsInvoice_JP_ds.reread();
    ledgerJournalTable_ds.research();
    ledgerJournalTrans_ds.research();

    element.updateDesign();
}

/// <summary>
/// Adds customer invoice journals to an existed consolidated invoice.
/// </summary>
public void updateConsInvoice()
{
    Query                   query;
    QueryRun                queryRun;
    CustConsInvoiceType_JP  custConsInvoiceType;

    custConsInvoiceType = CustConsInvoiceType_JP::construct(custConsInvoice_JP);
    query = custConsInvoiceType.buildUpdateQuery();

    queryRun = new QueryRun(query);
    if (queryRun.prompt())
    {
        CustConsInvoiceType_JP::update(custConsInvoice_JP, queryRun.query());
        custConsInvoice_JP_ds.reread();
        custConsInvoice_JP_ds.refresh();
        custInvoiceJour_W_ds.research(true);
        custInvoiceJour_ds.research(true);
    }
}

/// <summary>
/// Enables or disables buttons according to consolidated invoice status.
/// </summary>
public void updateDesign()
{
    boolean isConfirmed;

    if (custConsInvoice_JP.isConsConfirmed())
    {
        isConfirmed = true;
    }

    if(TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled(false))
    {
        Posting_ButtonGroup.visible(true);
        ConsInvoice_ButtonGroup.visible(true);
        TaxTransSource.visible(true);
        CustConsInvoice_JP_ds.object(fieldNum(CustConsInvoice_JP, Posted)).visible(true);

        MenuItemButtonConfirm.visible(false);
        MenuItemButtonUnconfirm.visible(false);
        TaxTransactions.visible(false);

        menuItemButtonConfirmV2.enabled(!isConfirmed);
        menuItemButtonUnconfirmV2.enabled(isConfirmed && !custConsInvoice_JP.JournalNum);

        MenuItemButtonPost.enabled(isConfirmed && !custConsInvoice_JP.Posted);
        TransactionReversalDialogButton.enabled(custConsInvoice_JP.Posted && custConsInvoice_JP.JournalNum);
    }
    else
    {
        Posting_ButtonGroup.visible(false);
        ConsInvoice_ButtonGroup.visible(false);
        TaxTransSource.visible(false);
        CustConsInvoice_JP_ds.object(fieldNum(CustConsInvoice_JP, Posted)).visible(false);

        MenuItemButtonConfirm.visible(true);
        MenuItemButtonUnconfirm.visible(true);
        TaxTransactions.visible(true);

        menuItemButtonConfirm.enabled(!isConfirmed);
        menuItemButtonUnconfirm.enabled(isConfirmed);
    }

    AddInvoiceButton.enabled(!isConfirmed);
    DeleteInvoiceButton.enabled(!isConfirmed);

    custConsInvoice_JP_ds.allowDelete(!isConfirmed);
    custConsInvoice_JP_ds.object(fieldNum(CustConsInvoice_JP, ExecutionDate)).enabled(!isConfirmed);
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    return ret;
}

public void delete()
{
    super();

    researchData = true;

    if (!CustConsInvoice_JP_ds.cursor().RecId)
    {
        CustConsInvoice_JP_ds.research(researchData);
    }
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(CustInvoiceJour_W)).addDynalink(
        fieldNum(CustInvoiceJour_W, CustConsInvoice_JP),
        custConsInvoice_JP,
        fieldNum(CustConsInvoice_JP, RecId));
}

void clicked()
{
    Args argsLoc;
    argsLoc = new Args();
    argsLoc.record(custConsInvoice_JP);

    CustConsolidatedInvoiceCreation_JP::main(argsLoc);

    custConsInvoice_JP_ds.research();

    super();
}

/// <summary>
/// This button intiates the consolidated invoice posting flow
/// </summary>
public void clicked()
{
    super();

    element.refreshAfterPostAndReversal();
}

public void clicked()
{
    if (custConsInvoice_JP.Posted)
    {
        super();
    }
    else
    {
        TaxConsInvoice_JP tax = TaxConsInvoice_JP::newForSourceTable(custConsInvoice_JP);
        tax.validateSalesTaxTransactionProperties(false);
        tax.calculateTax();
        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkConsInvoice_JP), tax, custConsInvoice_JP, element);
    }
}

public void clicked()
{
    List invoiceJourToRemove = new List(Types::Int64);
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(CustInvoiceJour_ds);

    super();

    CustInvoiceJour custInvoiceJourToRemove = helper.getFirst();

    while (custInvoiceJourToRemove)
    {
        invoiceJourToRemove.addEnd(custInvoiceJourToRemove.RecId);
        custInvoiceJourToRemove = helper.getNext();
    }

    CustVendConsInvoiceType_JP::removeInvoiceJour(tablenum(custInvoiceJour), invoiceJourToRemove);

    custConsInvoice_JP_ds.reread();
    custConsInvoice_JP_ds.refresh();
    custInvoiceJour_W_ds.research();
    custInvoiceJour_ds.research();
    if(TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled(false))
    {
        ledgerJournalTable_ds.research();
        ledgerJournalTrans_ds.research();
        custConsInvoiceHistory_JP_ds.research();
    }
}
