/// <summary>
/// Adds the <c>QueryFilters</c> for the <c>SysQueryForm</c>.
/// </summary>
/// <param name="_queryRun">
/// The <c>QueryRun</c> object to add the <c>QueryFilters</c> to.
/// </param>
private void addQueryFilters(QueryRun _queryRun)
{
    _queryRun.query().clearQueryFilters();

    custInvoiceTableqbds = _queryRun.query().dataSourceTable(tableNum(CustInvoiceTable));
    custDirectiveqbds = _queryRun.query().dataSourceTable(tableNum(CustDirective_PSN));
    custRoutingSlipqbds = _queryRun.query().dataSourceTable(tableNum(CustRoutingSlip_PSN));

    // Add a query filter for the InvoiceAccount field
    filter = _queryRun.query().addQueryFilter(custInvoiceTableqbds, fieldStr(CustInvoiceTable, InvoiceAccount));

    // Add a query filter for the BudgetaryAccountNum field
    filter = _queryRun.query().addQueryFilter(custDirectiveqbds, fieldStr(CustDirective_PSN, CustDirectiveBudgetaryAccount));

    // Add a query filter for the CustDirectiveId field
    filter = _queryRun.query().addQueryFilter(custDirectiveqbds, fieldStr(CustDirective_PSN, CustDirectiveId));

    // Add a query filter for the CustRoutingSlipId field
    filter = _queryRun.query().addQueryFilter(custRoutingSlipqbds, fieldStr(CustRoutingSlip_PSN, CustRoutingSlipId));

    // Add a query filter for the DirectorApprovalStatus field
    filter = _queryRun.query().addQueryFilter(custDirectiveqbds, fieldStr(CustDirective_PSN, DirectorApprovalStatus));

    // Add a query filter for the AccountApprovalStatus field
    filter = _queryRun.query().addQueryFilter(custDirectiveqbds, fieldStr(CustDirective_PSN, AccountantApprovalStatus));
}

/// <summary>
/// Builds the <c>SysQueryRun</c> used for the <c>SysQueryForm</c>.
/// </summary>
public void buildSysQuery()
{
    QueryBuildRange range;

    SysQueryRun queryRun = new SysQueryRun(CustInvoiceTable_ds.queryRun().query(), true);
    queryRun.title("@GLS223265");

    queryRun.promptLoadLastUsedQuery(false);
    queryRun.promptShowSorting(false);

    this.addQueryFilters(queryRun);

    if (queryRun.prompt())
    {
        //Clear the previously set ranges
        CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).clearRange(fieldNum(CustInvoiceTable, RecId));
        CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).clearRange(fieldNum(CustInvoiceTable, Posted));
        CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).clearRange(fieldNum(CustInvoiceTable, InvoiceDate));

        //Add the range for posted/unposted free text invoices
        if (InvoiceStatus.selection() == enum2int(CustVendDirectiveInvoiceStatus_PSN::Pending))
        {
            range = CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).addRange(fieldNum(CustInvoiceTable, Posted));
            range.value(enum2str(NoYes::No));
            range.status(RangeStatus::Hidden);
        }
        else
        {
            range = CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).addRange(fieldNum(CustInvoiceTable, Posted));
            range.value(enum2str(NoYes::Yes));
            range.status(RangeStatus::Hidden);
        }

        //Add the date range for invoice date
        range = CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).addRange(fieldNum(CustInvoiceTable, InvoiceDate));
        range.value(SysQueryRangeUtil::dateRange(DateTimeUtil::date(FromDate.dateTimeValue()), DateTimeUtil::date(ToDate.dateTimeValue())));
        range.status(RangeStatus::Hidden);

        CustInvoiceTable_DS.research();
    }
    else
    {
        CustInvoiceTable_DS.research();
    }

    this.setHeaderFieldsGroupControlValues(queryRun);
}

public void init()
{
    QueryBuildRange range;

    super();

    //Clear the Dynalinks to disable getting the context from Free text invoice list page & Free text invoice details
    CustInvoiceTable_DS.query().dataSourceTable(tableNum(CustInvoiceTable)).clearDynalinks();

    FromDate.dateTimeValue(DateTimeUtil::getSystemDateTime());
    ToDate.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::maxValue(), DateTimeUtil::getUserPreferredTimeZone()));

    //Add a range that will return no values to keep the grid from loading records on form launch
    range = CustInvoiceTable_DS.query().dataSourceName(identifierStr(CustInvoiceTable)).addRange(fieldNum(CustInvoiceTable, RecId));
    range.value('0');
    range.status(RangeStatus::Hidden);
}

/// <summary>
/// Disables the Directive fields if the DirectiveId has not been assigned in a
/// public sector France context.
/// </summary>
public void setFieldsEditableForDirective()
{
    boolean isDirectiveSet;

    if (CustVendDirective_PSN::isContextPublicSectorFrance())
    {
        isDirectiveSet = (CustDirective_PSN.CustDirectiveId != '');

        DirectorApprovalStatus.allowEdit(isDirectiveSet);
        AccountApprovalStatus.allowEdit(isDirectiveSet);
        IssueDate.allowEdit(isDirectiveSet);
    }
}

/// <summary>
/// Sets the field text based on the <c>QueryFilters</c> contained in the <c>QueryRun</c>.
/// </summary>
/// <param name="_queryRun">
/// A <c>QueryRun</c> containing the <c>QueryFilters</c>.
/// </param>
private void setHeaderFieldsGroupControlValues(QueryRun _queryRun)
{
    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), custInvoiceTableqbds, fieldId2name(tablenum(CustInvoiceTable), fieldName2id(tablenum(CustInvoiceTable), identifierStr(InvoiceAccount))));
    InvoiceAccounts.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), custDirectiveqbds, fieldId2name(tablenum(CustDirective_PSN), fieldName2id(tablenum(CustDirective_PSN), identifierStr(CustDirectiveBudgetaryAccount))));
    BudgetaryAccount.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), custDirectiveqbds, fieldId2name(tablenum(CustDirective_PSN), fieldName2id(tablenum(CustDirective_PSN), identifierStr(CustDirectiveId))));
    CustDirectiveID.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), custRoutingSlipqbds, fieldId2name(tablenum(CustRoutingSlip_PSN), fieldName2id(tablenum(CustRoutingSlip_PSN), identifierStr(CustRoutingSlipId))));
    CustRoutingSlipId.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), custDirectiveqbds, fieldId2name(tablenum(CustDirective_PSN), fieldName2id(tablenum(CustDirective_PSN), identifierStr(DirectorApprovalStatus))));
    DirectorStatus.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), custDirectiveqbds, fieldId2name(tablenum(CustDirective_PSN), fieldName2id(tablenum(CustDirective_PSN), identifierStr(AccountantApprovalStatus))));
    AccountantStatus.text(filter.value());
}

