/// <summary>
/// Clears the details grid when the invoice status radio button is toggled.
/// </summary>
/// <returns>
/// true if the invoice status radio button was toggled, otherwise; false.
/// </returns>
public int selectionChange()
{
    int             canChangeSelection;
    QueryBuildRange range;

    if (!CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).findRange(fieldNum(CustInvoiceTable, RecId)))
    {
        //Add a range that will return no records in order to clear the details grid when the invoice status radio button is toggled
        range = CustInvoiceTable_DS.queryRun().query().dataSourceName(identifierStr(CustInvoiceTable)).addRange(fieldNum(CustInvoiceTable, RecId));
        range.value('0');
        range.status(RangeStatus::Hidden);
    }

    canChangeSelection = super();

    if (canChangeSelection)
    {
        CustInvoiceTable_DS.research();
    }

    return canChangeSelection;
}

/// <summary>
/// Validates that the value in the <c>FromDate</c> field is not greater than the value in the <c>ToDate</c> field.
/// </summary>
/// <returns>
/// true if the value in the <c>FromDate</c> field is valid; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (isValid)
