/// <summary>
/// Creates a <c>CustDirective_PSN</c> record for every marked <c>CustInvoiceLine</c> record.
/// </summary>
void clicked()
{
    Array               recordsMarked;
    int                 lastIndex;
    int                 iLoop;
    CustDirective_PSN   custDirectiveLocal;
    CustInvoiceLine     custInvoiceLineLocal;

    recordsMarked = CustInvoiceTable_DS.recordsMarked();
    lastIndex = recordsMarked.lastIndex();

    for (iLoop =1; iLoop <= recordsMarked.lastIndex(); iLoop++)
    {
        custDirectiveLocal = CustDirective_PSN_DS.cursor(recordsMarked.value(iLoop));
        custInvoiceLineLocal = CustInvoiceLine_DS.cursor(recordsMarked.value(iLoop));

        if (custDirectiveLocal.CustDirectiveId == '')
        {
            ttsBegin;
            custDirectiveLocal.initValue();
            custDirectiveLocal.insert();

            custInvoiceLineLocal.CustDirective_PSN = custDirectiveLocal.RecId;
            custInvoiceLineLocal.update();
            ttsCommit;
        }
    }

    CustInvoiceTable_DS.research();
}

/// <summary>
/// Creates a <c>CustRoutingSlip_PSN</c> record for a collection of marked <c>CustInvoiceLine</c> records.
/// </summary>
void clicked()
{
    Array               recordsMarked;
    int                 lastIndex;
    int                 iLoop;
    CustDirective_PSN   custDirectiveLocal;
    CustRoutingSlip_PSN custRoutingSlipLocal;

    recordsMarked = CustInvoiceTable_DS.recordsMarked();
    lastIndex = recordsMarked.lastIndex();

    for (iLoop =1; iLoop <= recordsMarked.lastIndex(); iLoop++)
    {
        custDirectiveLocal = CustDirective_PSN_DS.cursor(recordsMarked.value(iLoop));

        if ((custDirectiveLocal.CustDirectiveId) && (!custDirectiveLocal.CustRoutingSlip_PSN))
        {
            if (!custRoutingSlipLocal)
            {
                ttsBegin;
                custRoutingSlipLocal.initValue();
                custRoutingSlipLocal.insert();
                ttsCommit;
            }

            ttsBegin;
            custDirectiveLocal.CustRoutingSlip_PSN = custRoutingSlipLocal.RecId;
            custDirectiveLocal.update();
            ttsCommit;
        }
    }

    CustInvoiceTable_DS.research();
}

public boolean modified()
{
    boolean canModify;

    canModify = super();

    if (canModify)
    {
        CustInvoiceTable_DS.research(true);
    }

    return canModify;
}
