[Form]
public class CustFormletterParameters extends FormRun
{
    boolean inventDimParmAllowCreateConfirmation;
    boolean inventDimParmAllowCreateInvoice;
    boolean inventDimParmAllowCreatePackingSlip;
    boolean inventDimParmAllowCreatePickingList;
    boolean inventDimParmAllowCreateQuotation;
    boolean inventDimParmAllowCreateAgreementConfirm;
    // <GEERU>
    boolean inventDimParmAllowCreateInvoice4Paym_RU;
    boolean isRU;
    // </GEERU>

    #define.WordExtension("*.dot")

}

FilenameFilter fileNameLookupFilter()
{
    FilenameFilter filenameFilter;

    filenameFilter = conIns(filenameFilter,
                            1,
                            "@GLS102738",
                            #WordExtension,
                            "@SYS27373",
                            '*.*');
    return filenameFilter;
}

public void init()
{
    List            inventDimAgreementFieldList = InventDim::dimAgreementFieldList();
    ListEnumerator  fieldListEnumerator = inventDimAgreementFieldList.getEnumerator();
    FieldId         fieldId;

    boolean         isVisible;

    #ISOCountryRegionCodes
    // <GEERU>
    isRU = SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]);
    // </GEERU>

    inventDimParmAllowCreateConfirmation     = true;
    inventDimParmAllowCreateInvoice          = true;
    inventDimParmAllowCreatePackingSlip      = true;
    inventDimParmAllowCreatePickingList      = true;
    inventDimParmAllowCreateQuotation        = true;
    inventDimParmAllowCreateAgreementConfirm = true;

    // <GEERU>
    inventDimParmAllowCreateInvoice4Paym_RU = isRU;
    // </GEERU>

    super();

    CustFormletterParameters::find();
    CustFormletterDocument::find();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT]))
    {
        custFormletterParameters_ds.object(fieldNum(CustFormletterParameters, TransportationDocumentOnPackingSlip)).allowEdit(SalesParameters::find().TransportationDocumentOnPackingSlip);
    }

    while (fieldListEnumerator.moveNext())
    {
        fieldId = fieldListEnumerator.current();
        inventDimParmAgreementConfirm_DS.object(InventDim::dim2dimParm(fieldId)).enabled(true);
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR]))
    {
        isVisible = isConfigurationKeyEnabled(configurationkeynum(LedgerBasic))
            && !(isConfigurationkeyEnabled(configurationkeynum(LogisticsBasic)));

        DocuFreeTextInvoice_DocuOverdueFineTxtInvoice_FR.visible(isVisible);
        DocuFreeTextInvoice_DocuOverdueFineNumOfDaysInvoice_FR.visible(isVisible);
    }
}

/// <summary>
/// Initializes the <c>PrintMgmtSetupContext</c> object.
/// </summary>
/// <returns>The newly initialized <c>PrintMgmtSetupContext</c> object.</returns>
protected PrintMgmtSetupContext initializePrintManagementSetupContext()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    List inventDocs = new List(Types::Enum);
    inventDocs.addEnd(PrintMgmtDocumentType::InventPickList);

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Sales, PrintMgmtNodeType::Sales);
    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Invent, PrintMgmtNodeType::Invent, inventDocs);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(null);

    return setupContext;
}

public void run()
{
    super();

    inventDimParmConfirmation_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimConfirm,inventDimParmConfirmation);
    inventDimParmConfirmation.write();

    inventDimParmInvoice_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimInvoice,inventDimParmInvoice);
    inventDimParmInvoice.write();

    inventDimParmPackingSlip_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimPackingSlip,inventDimParmPackingSlip);
    inventDimParmPackingSlip.write();

    inventDimParmPickinglist_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimPickingList,inventDimParmPickinglist);
    inventDimParmPickinglist.write();

    inventDimParmQuotation_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimQuotation,inventDimParmQuotation);
    inventDimParmQuotation.write();

    inventDimParmAgreementConfirm_DS.create();
    InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimSalesAgreement,inventDimParmAgreementConfirm);
    inventDimParmAgreementConfirm.write();

    // <GEERU>
    if (isRU)
    {
        inventDimParmInvoice4Paym_RU_DS.create();
        InventDimFixedClass::inventDimFixed2InventDimParm(custFormletterParameters.InventDimInvoice4Paym_RU, inventDimParmInvoice4Paym_RU);
        inventDimParmInvoice4Paym_RU.write();
    }
    // </GEERU>
}

public void write()
{
    custFormletterParameters.InventDimConfirm        = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmConfirmation);
    custFormletterParameters.InventDimInvoice        = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmInvoice);
    custFormletterParameters.InventDimPackingSlip    = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmPackingSlip);
    custFormletterParameters.InventDimPickingList    = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmPickingList);
    custFormletterParameters.InventDimQuotation      = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmQuotation);
    custFormletterParameters.InventDimSalesAgreement = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmAgreementConfirm);
      // <GEERU>
    custFormletterParameters.InventDimInvoice4Paym_RU = InventDimFixedClass::inventDimParm2InventDimFixed(inventDimParmInvoice4Paym_RU);
    // </GEERU>

    super();
}

