[Form]
public class CustFreeCreditNote_W extends FormRun
{
    TmpFrmVirtual        tmpFrmVirtualLines;
    TmpFrmVirtual        tmpFrmVirtualHeader;

    CustInvoiceTable     invoiceTable, tmpCustInvoiceTable;
    FormDataSource       callerDataSource;
    boolean              headerReasonCodeExsists;

}

public boolean canClose()
{
    boolean                                canClose;
    RecordSortedList                       rsl;
    CustFreeInvoiceCreditNote_RU           creditNote;
    boolean                                copyExchRate = CopyExchangeRate.value();
    RecId                                  correctionReasonCode;

    canClose = super();

    if (element.closedOk() && canClose)
    {
        if (callerDataSource.allowEdit() && ! invoiceTable.InvoiceId)
        {
            if (copyExchRate)
            {
                select firstonly tmpFrmVirtualHeader;
                copyExchRate = tmpFrmVirtualHeader.RecId != 0;
            }

            rsl = new RecordSortedList(tableNum(CustInvoiceTrans));
            rsl.sortOrder(fieldNum(CustInvoiceTrans, RecId));

            while select tmpFrmVirtualLines
                order by Num
            {
                rsl.ins(CustInvoiceTrans::findRecId(tmpFrmVirtualLines.RecordNo));
            }
            if (rsl.len())
            {
                if (headerReasonCodeExsists)
                {
                    correctionReasonCode = tmpCustInvoiceTable.CorrectionReasonCode;
                }
                else
                {
                    correctionReasonCode = invoiceTable.CorrectionReasonCode;
                }

                creditNote = CustFreeInvoiceCreditNote_RU::newInvoiceCreditNote(invoiceTable.RecId,
                                                                                correctionReasonCode);
                creditNote.parmCustInvoiceTransList(rsl);
                creditNote.parmCopyExchRate(copyExchRate);
                creditNote.parmCreateCorrectiveLines(createCorrectiveLines.value());
                canClose = creditNote.createCreditNote();
            }

            callerDataSource.reread();
            callerDataSource.active();
        }
        else
        {
            canClose = checkFailed("@GLS109941");
        }
    }
    return canClose;
}

