void init()
{
    // <GEEU>
    #ISOCountryRegionCodes
    // </GEEU>

    if (!element.args().record() || !FormDataUtil::getFormDataSource(element.args().record()))
    {
        throw error("@SYS22539");
    }

    invoiceTable     = element.args().record();
    callerDataSource = FormDataUtil::getFormDataSource(element.args().record());

    if (invoiceTable.CorrectionReasonCode)
    {
        headerReasonCodeExsists = true;
    }

    if (!invoiceTable.RecId)
    {
        throw error("@GLS109943");
    }

    super();

    GlobalizationInstrumentationHelper::featureRunByCountryRegionCodes(
                [ [#isoPL, GlobalizationConstants::FeatureReferencePL00004] ],
                funcName()
            );

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        createCorrectiveLines.value(false);
        createCorrectiveLines.visible(false);
    }
    else if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL,#isoHU]))
    {
        createCorrectiveLines.value(true);
        GlobalizationInstrumentationHelper::featureRunByCountryRegionCodes([
            [#isoHU, GlobalizationConstants::FeatureReferenceHU00005] ],
            funcName()
        );
    }
    else if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ,#isoLT,#isoLV,#isoEE]))
    {
        createCorrectiveLines.value(false);
    }

    CopyExchangeRate.value(CopyExchangeRate.isVisible());

    if (CustParameters::find().CreditNoteReasonReq)
    {
        editReasonComment.mandatory(true);
    }
    else
    {
        editReasonComment.mandatory(false);
    }
}

public int active()
{
    int ret = super();

    custInvoiceJour_DS.allowEdit(callerDataSource.allowEdit());

    return ret;
}

void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceTable(tableNum(CustInvoiceJour));

    qbds.findRange(fieldNum(CustInvoiceJour, InvoiceAccount)).value(invoiceTable.InvoiceAccount);
    qbds.findRange(fieldNum(CustInvoiceJour, OrderAccount)).value(invoiceTable.OrderAccount);
    qbds.findRange(fieldNum(CustInvoiceJour, CurrencyCode)).value(invoiceTable.CurrencyCode);

    qbds.findRange(fieldNum(CustInvoiceJour, InvoiceId)).value(SysQuery::valueNotEmptyString());
    qbds.findRange(fieldNum(CustInvoiceJour, RefNum)).value(SysQuery::value(RefNum::Cust));

    super();
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(CustInvoiceJour));

    qbds.clearDynalinks();

    qbds.addRange(fieldNum(CustInvoiceJour, InvoiceAccount)).status(RangeStatus::Locked);
    qbds.addRange(fieldNum(CustInvoiceJour, OrderAccount)).status(RangeStatus::Locked);
    qbds.addRange(fieldNum(CustInvoiceJour, InvoiceId)).status(RangeStatus::Locked);
    qbds.addRange(fieldNum(CustInvoiceJour, RefNum)).status(RangeStatus::Locked);
    qbds.addRange(fieldNum(CustInvoiceJour, CurrencyCode)).status(RangeStatus::Locked);
}

/// <summary>
/// Mark all invoice from CustInvoiceJour.
/// </summary>
/// <param name = "_set">
/// A Boolean value that indicates whether to get query from CustInvoiceTrans.
/// </param>
/// <param name = "_custInvoiceJour">
/// An instance of CustInvoiceJour.
/// </param>
/// <param name = "_markAll">
/// A Boolean value that indicates whether to mark all invoice list in grid.
/// </param>
/// <returns>
/// All invoices have been marked or not.
/// </returns>
edit NoYes invoiceMarkAll(boolean         _set,
                          CustInvoiceJour _custInvoiceJour,
                          NoYes           _markAll)
{
    QueryRun                   qR;
    NoYes                      markAll;
    AgreementHeaderExtRecId_RU agreementHeaderExt;

    select tmpFrmVirtualHeader
        where tmpFrmVirtualHeader.TableNum == _custInvoiceJour.TableId &&
              tmpFrmVirtualHeader.RecordNo == _custInvoiceJour.RecId;

    if (tmpFrmVirtualHeader)
        markAll   = NoYes::Yes;

    if (_set)
    {
        qR = new QueryRun(custInvoiceTrans_ds.query());

        if (_markAll)
        {
            agreementHeaderExt = AgreementHeaderExt_RU::findByDefaultDimension(_custInvoiceJour.DefaultDimension).RecId;
            if (agreementHeaderExt && invoiceTable.AgreementHeaderExt_RU && agreementHeaderExt != invoiceTable.AgreementHeaderExt_RU)
            {
                checkFailed("@GLS222712");
                markAll= NoYes::No;
            }
            else
            {
                tmpFrmVirtualHeader.TableNum   =  _custInvoiceJour.TableId;
                tmpFrmVirtualHeader.RecordNo   =  _custInvoiceJour.RecId;
                tmpFrmVirtualHeader.Id         =  _custInvoiceJour.InvoiceId;

                tmpFrmVirtualHeader.write();
                markAll = NoYes::Yes;

                while (qR.next())
                {
                    if (this.isPreviouslyCorrected_W(qR.get(tableNum(CustInvoiceTrans))))
                    {
                        continue;
                    }

                    custInvoiceTrans_DS.copy(true, qR.get(tableNum(CustInvoiceTrans)), true);
                }
            }
        }
        else
        {
            tmpFrmVirtualHeader.delete();
            markAll = NoYes::No;

            while (qR.next())
            {
                custInvoiceTrans_DS.copy(true, qR.get(tableNum(CustInvoiceTrans)), false);
            }
        }

        CustInvoiceTrans_DS.research();
    }
    return markAll;
}

private boolean isPreviouslyCorrected_W(CustInvoiceTrans _custInvoiceTrans)
{
    boolean ret = true;
    CustInvoiceTrans localCustInvoiceTrans = _custInvoiceTrans;

    if (localCustInvoiceTrans.Qty >= 0)
    {
        CustInvoiceLine tmpCustInvoiceLine;
        CustRelatedInvoice tmpCustRelatedInvoice;

        select firstonly RecId from tmpCustInvoiceLine
            where tmpCustInvoiceLine.RefReturnInvoiceTrans_W == localCustInvoiceTrans.RecId
            notexists join tmpCustRelatedInvoice
                where tmpCustRelatedInvoice.CustInvoice == tmpCustInvoiceLine.ParentRecId
                    && (tmpCustRelatedInvoice.InvoiceAssociationType == InvoiceAssociationType::OriginalInvoice
                    || tmpCustRelatedInvoice.InvoiceAssociationType == InvoiceAssociationType::AdjustingInvoice);

        if (!tmpCustInvoiceLine)
        {
            CustInvoiceTrans tmpCustInvoiceTrans;
            CustInvoiceTrans_W tmpCustInvoiceTrans_W;

            select firstonly RecId from tmpCustInvoiceTrans
                exists join tmpCustInvoiceTrans_W
                where tmpCustInvoiceTrans_W.CustInvoiceTrans == tmpCustInvoiceTrans.RecId
                    && tmpCustInvoiceTrans_W.RefReturnInvoiceTrans_W == localCustInvoiceTrans.RecId;

            if (!tmpCustInvoiceTrans)
            {
                ret = false;
            }
        }
    }

    return ret;
}

/// <summary>
/// Copy all invoice from CustInvoiceTrans.
/// </summary>
/// <param name = "_set">
/// A Boolean value that indicates whether to get query from CustInvoiceTrans.
/// </param>
/// <param name = "_custInvoiceTrans">
/// An instance of CustInvoiceTrans.
/// </param>
/// <param name = "_copy">
/// A Boolean value that indicates whether to mark all invoice lines list in grid.
/// </param>
/// <returns>
/// All invoice lines have been copied or not.
/// </returns>
edit NoYes copy(boolean          _set,
                CustInvoiceTrans _custInvoiceTrans,
                NoYes            _copy)
{
    NoYes                      copy;
    AgreementHeaderExtRecId_RU agreementHeaderExt;

    select firstonly tmpFrmVirtualLines
        where tmpFrmVirtualLines.TableNum == _custInvoiceTrans.TableId &&
              tmpFrmVirtualLines.RecordNo == _custInvoiceTrans.RecId;

    if (tmpFrmVirtualLines)
    {
        copy = NoYes::Yes;
    }

    if (_set)
    {
        agreementHeaderExt = AgreementHeaderExt_RU::findByDefaultDimension(_custInvoiceTrans.DefaultDimension).RecId;
        if (_copy                                                                                   &&
            ! _custInvoiceTrans.custInvoiceTrans_W().RefReturnInvoiceTrans_W                        &&
            ! CustInvoiceLine::creditNoteExist_W(_custInvoiceTrans.RecId)                           &&
            ! CustRelatedInvoice::doesInvoiceExistForType(_custInvoiceTrans.custInvoiceJour().custInvoiceTable().RecId,
                                                          InvoiceAssociationType::AdjustingInvoice) &&
            ! CustRelatedInvoice::doesInvoiceExistForType(_custInvoiceTrans.custInvoiceJour().custInvoiceTable().RecId,
                                                          InvoiceAssociationType::OriginalInvoice) &&
            (createCorrectiveLines.value() == NoYes::No || (createCorrectiveLines.value() == NoYes::Yes && !(_custInvoiceTrans.LineAmount < 0))) &&
            (!agreementHeaderExt || !invoiceTable.AgreementHeaderExt_RU || agreementHeaderExt == invoiceTable.AgreementHeaderExt_RU))
        {
            tmpFrmVirtualLines.TableNum   = _custInvoiceTrans.TableId;
            tmpFrmVirtualLines.RecordNo   = _custInvoiceTrans.RecId;
            tmpFrmVirtualLines.Id         = _custInvoiceTrans.InvoiceId;
            tmpFrmVirtualLines.TransDate  = _custInvoiceTrans.InvoiceDate;

            tmpFrmVirtualLines.write();
            copy = NoYes::Yes;
        }
        else
        {
            if (_copy)
            {
                this.checkCreditNoteExist_W(_custInvoiceTrans);
            }

            tmpFrmVirtualLines.delete();
            copy = NoYes::No;
        }
    }

    return copy;
}

private boolean checkCreditNoteExist_W(CustInvoiceTrans _custInvoiceTrans)
{
    boolean ret;

    if (_custInvoiceTrans)
    {
        CustInvoiceLine custInvoiceLine;
        CustRelatedInvoice custRelatedInvoice;

        select firstonly RecId, LineNum, ParentRecId from custInvoiceLine
            where custInvoiceLine.RefReturnInvoiceTrans_W == _custInvoiceTrans.RecId
            notexists join custRelatedInvoice
                where custRelatedInvoice.CustInvoice == custInvoiceLine.ParentRecId 
                    && (custRelatedInvoice.InvoiceAssociationType == InvoiceAssociationType::OriginalInvoice 
                    || custRelatedInvoice.InvoiceAssociationType == InvoiceAssociationType::AdjustingInvoice);

        if (CustInvoiceLine)
        {
            ret = !checkFailed(strFmt("@GLS220578", CustInvoiceLine.LineNum, CustInvoiceTable::findRecId(custInvoiceLine.ParentRecId).InvoiceId));
        }
    }

    return ret;
}

