/// <summary>
/// Set the accessibility of <c>CustBillingClassification</c> field of the <c>CustInvoiceTable</c> table.
/// </summary>
void allowEdit_CustBillingClassification()
{
    if (isBillingClassificationEnabled)
    {
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable,CustBillingClassification)).allowEdit(
                                        !custInvoiceLine.existCustBillingCode(custInvoiceTable.RecId));
    }
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret && custInvoiceTable.Touched)
    {
        ret = custInvoiceTable.checkCreditLimit();
    }

    return ret;
}

/// <summary>
/// Checks to see if there are any custom fields assoicated with the billing code
/// </summary>
/// <returns>
/// True if the dialog can be shown; otherwise false;
/// </returns>
public boolean canShowInputDialog()
{
    boolean canShow = false;
    CustBillingCodeCustomField custBillingCodeCustomField;

    custBillingCodeVersion = CustBillingCodeVersion::findByDate(custInvoiceLine.CustBillingCode, custInvoiceTable.InvoiceDate);

    if (!customFieldDialogOpen)
    {
        select firstonly custBillingCodeCustomField
        where custBillingCodeCustomField.CustBillingCodeVersion == custBillingCodeVersion.RecId
              && custBillingCodeCustomField.IsActive == true;

        if (!custBillingCodeCustomField.RecId)
        {
            select firstonly RecId, CustBillingCodeVersion from custBillingCodeCustomField
                where
                    custBillingCodeCustomField.CustBillingCodeVersion == custBillingCodeVersion.RecId;

            if (custBillingCodeCustomField.RecId && custInvoiceTable.Posted)
            {
                canShow = true;
            }
        }
        else
        {
            canShow = true;
        }
    }
    else
    {
        canShow = false;
    }

    return canShow;
}

public boolean canSubmitToWorkflow()
{
    return custInvoiceTable.canSubmitToWorkflow();
}

/// <summary>
/// This method is called by a <c>CustCustomFieldInputDialog</c> instance when it is closed.
/// </summary>
/// <param name="_mapCustomFieldElements">
/// A <c>Map</c> containg a list of <c>CustCustomFieldElement</c> instances.
/// </param>
/// <param name="_okClicked">
/// A true value if the ok button was clicked otherwise a false value.
/// </param>
/// <remarks>
/// This method resets all the defaults that were set by selecting a billing code if cancel is clicked and the billing code was empty.
/// </remarks>
public void closeCustomFieldDialog(Map _mapCustomFieldElements, boolean _okClicked)
{
    if (_okClicked && !custInvoiceTable.Posted)
    {
        mapCustCustomFieldElements = _mapCustomFieldElements;
        if (!_mapCustomFieldElements.empty())
        {
            custInvoiceLine_ds.forceWrite(true);
        }
    }

    if (custInvoiceLine.orig().CustBillingCode != custInvoiceLine.CustBillingCode && !_okClicked)
    {
        custInvoiceLine.Description = custInvoiceLine.orig().Description;
        custInvoiceLine.TaxItemGroup = custInvoiceLine.orig().TaxItemGroup;
        custInvoiceLine.AmountDetails = custInvoiceLine.orig().AmountDetails;
        custInvoiceLine.InvoiceTxt = custInvoiceLine.orig().InvoiceTxt;
        custInvoiceLine.CustBillingCode = custInvoiceLine.orig().CustBillingCode;
        custInvoiceLine.BillingCode = custInvoiceLine.orig().BillingCode;
    }
    customFieldDialogOkClicked = _okClicked;

    buttonCustomField.enabled(true);
}

/// <summary>
/// Creates one or more copies of all lines currently selected.
/// </summary>
/// <param name = "_numberOfCopies">
/// The number of copies to make for each selected line.
/// </param>
/// <param name = "_copyLineItems">
/// A list of line items such as notes and distributions that should be copied to the new line.
/// </param>
public void copyLinesMultipleItems(int _numberOfCopies, container _copyLineItems)
{
    boolean isLineCreated;

    // Determine number of selected lines
    int numberOfSelectedLines = custInvoiceLine_ds.recordsMarked().lastIndex();

    if (numberOfSelectedLines == 0 && custInvoiceLine)
    {
        numberOfSelectedLines++;
    }

    // Do not allow the product of the number of selected lines and the number of copies chosen by the user to exceed the maximum allowed
    if ((_numberOfCopies > this.maxNumberOfLinesToCopy() && numberOfSelectedLines > 0) || (_numberOfCopies * numberOfSelectedLines > this.maxNumberOfLinesToCopy()))
    {
        error(strFmt("@AccountsReceivable:ExceededMaximumNumberOfLinesToCopy", this.maxNumberOfLinesToCopy()));
        return;
    }

    ttsbegin;

    for (int i = 1; i <= _numberOfCopies; i++)
    {
        MultiSelectionHelper selectionHelper = MultiSelectionHelper::construct();
        selectionHelper.parmDataSource(custInvoiceLine_ds);

        CustInvoiceLine custInvoiceLineOrig = selectionHelper.getFirst();
        while (custInvoiceLineOrig)
        {
            isLineCreated = true;
            custInvoiceLineOrig.createCopy(custInvoiceTable, _copyLineItems);
            custInvoiceLineOrig = selectionHelper.getNext();
        }
    }

    ttscommit;

    if (isLineCreated)
    {
        // require the taxes to be recreated since new lines were created
        recreateTax = true;
        element.deleteTaxForDocumentHeader();

        // Show the new line(s) in the grid
        custInvoiceLine_ds.research(true);
    }
}

/// <summary>
/// Returns the maximum number of lines that can be copied using the copy lines drop dialog.
/// </summary>
/// <returns>
/// The maximum number of lines that can be copied using the copy lines drop dialog.
/// </returns>
public int maxNumberofLinesToCopy()
{
    return maxNumberOfLinesToCopy;
}

/// <summary>
/// Deletes taxes if they need to be recreated.
/// </summary>
public void deleteTaxForDocumentHeader()
{
    CustFreeInvoiceCalcTotals custFreeInvoiceCalcTotals;

    if (recreateTax)
    {
        if (!TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
        {
            TaxUncommitted::deleteForDocumentHeader(tableNum(CustInvoiceTable), custInvoiceTable.RecId);
        }

        if (countryRegion_EEU && deleteMarkupDistribution)
        {
            custFreeInvoiceCalcTotals = new CustFreeInvoiceCalcTotals(custInvoiceTable);
            custFreeInvoiceCalcTotals.calc();

            deleteMarkupDistribution = false;

            element.updateMarkupDistribution_W();
        }
    }
    if (TaxWithholdParameters_IN::checkTaxParameters())
    {
        element.deleteTaxWithholdForDocumentHeader_IN();
    }
}

/// <summary>
/// Deletes taxes if they need to be recreated.
/// </summary>
public void deleteTaxWithholdForDocumentHeader_IN()
{
    if (recreateWithholdTax)
    {
        TaxWithholdUncommitted_IN::deleteForDocumentHeader(tableNum(CustInvoiceTable), custInvoiceTable.RecId);
    }
}

public void enableCreditInvoicingButton()
{
    boolean isCreditInvoiceingButtonVisible = CreditInvoicingToggle::isCreditInvoicingEnabled();

    setupCustVendCreditInvoicingAction.visible(isCreditInvoiceingButtonVisible);

    if (isCreditInvoiceingButtonVisible && CustVendCreditInvoicingTable::useCreditInvoicingReporting())
    {
        CustInvoiceLine negativeCustInvoiceLine;

        select firstonly RecId from negativeCustInvoiceLine
            where negativeCustInvoiceLine.ParentRecId == custInvoiceTable.RecId
                && negativeCustInvoiceLine.AmountCur < 0;

        // Toggle header button
        setupCustVendCreditInvoicingAction.enabled(negativeCustInvoiceLine.RecId
            || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT, #isoID])
            || FeatureStateProvider::isFeatureEnabled(CreditInvoicingAllowDebitNoteFeature::instance()));

        // Toggle line button
        cvSetupCustVendCreditInvoicingAction.enabled(custInvoiceLine.AmountCur < 0);
    }
}

void enableDistButtonBasedOnAmountCur()
{
    if (str2num(custInvoiceLine_AmountCur.valueStr()) != 0)
    {
        accountingDistributionsLineButton.enabled(true);
    }
    else
    {
        accountingDistributionsLineButton.enabled(false);
    }
}

void enableLineChargeButtonByLedgerDimension()
{
    if (custInvoiceLine.LedgerDimension)
    {
        lineChargeButton.enabled(true);
    }
    else
    {
        lineChargeButton.enabled(false);
    }
}

void enableLineFiscalDocumentText_BR()
{
    lineFiscalDocumentText_BR.enabled(custInvoiceLine.RecId);
}

void init()
{
    boolean isFrenchRegulatoryEnabled = PublicSectorUtils::isFrenchRegulatoryEnabled();

    isBillingClassificationEnabled = CustParameters::isBillingClassificationEnabled();

    // <GMY>
    isCustInvoiceTable_WEnabled = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(CustInvoiceTable_W));
    isIsoMY = TaxGSTFeatureChecker_MY::isCountryRegionMY();
    // </GMY>

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        mcrCallCenterEnabled = true;

        if (element.args().menuItemName() == menuitemDisplayStr(MCRGiftCardCustFreeInvoiceDetails))
        {
            mcrGiftCardFreeTextInvoice = true;
        }
    }

    super();

    custVendInvoiceAttachments = CustVendInvoiceAttachments::construct();

    this.hideSystemDefinedAttachmentsButton();

    TaxVATNumTable::enableLookupVATNum(vatNum);

    groupNotificationToTheCentralBank.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]));
    if (!TaxParameters::find().CashDiscOnInvoice)
    {
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, CashDiscPercent)).allowEdit(false);
    }
    this.initDesign_RU();
    this.initDesign_HU();

    custInvoiceLine_LedgerAccount.parmPostingType(LedgerPostingType::CustRevenue);

    // Disable Invoice Correction for Spanish companies.
    adjustmentAction.visible(!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoES]));

    custParameters = CustParameters::find();

    if (mcrCallCenterEnabled)
    {
        if (element.args().menuItemName() == menuitemDisplayStr(MCRGiftCardCustFreeInvoiceDetails))
        {
            element.design().caption("@MCR34113");
        }

        userAllowsPayments = false;  // Call Center does not support Free Text Invoice, Dynamics bug #3839717
        mcrCustPaymTableButton.visible(userAllowsPayments);
        custInvoiceTable_MCRGetPaymStatus.visible(userAllowsPayments);
    }

    // Needed to check validity of address
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));

    cvInvoiceDetailsEInvoiceTabPage_MX.visible(EInvoiceCFDIParameters_MX::isElectronicInvoiceEnabled());
    cvInvoiceDetailsCFDITabPage_MX.visible(EInvoiceCFDIParameters_MX::isElectronicInvoiceEnabled());

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisible_IN();
        element.setTaxWithholdVisible_IN();
    }

    salesPurchOperationTypeController_BR = new SalesPurchOperationTypeController_BR(this.name());
    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, FiscalEstablishment_BR)).mandatory(BrazilParameters::isEnabled());
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    isUnrealizedVATEnabled = TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled();
    if (isUnrealizedVATEnabled)
    {
        this.updateDesign();
    }
    countryRegion_EEU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU,#isoPL,#isoHU,#isoCZ,#isoLT,#isoLV,#isoEE]);
    isPolishCountryContext = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]);

    editReasonCodeHeader.visible(true);
    editReasonCommentHeader.visible(true);

    countryRegion_LTLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT,#isoLV]);

    if (!CustParameters::isTaxIntegrationSystem_CN())
    {
        headVAT.visible(false);
        lineVAT.visible(false);
    }

    // <GMY>
    if (isIsoMY)
    {
        custInvoiceJourLookupController = CustInvoiceJourLookupController_MY::construct();
        editReasonCodeHeader.visible(true);
        editReasonCommentHeader.visible(true);
    }
    // </GMY>

    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, CustBillingClassification)).visible(isBillingClassificationEnabled);
    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, CustBillingClassification)).mandatory(isBillingClassificationEnabled);
    customFieldsButtons.visible(isBillingClassificationEnabled);
    custInvoiceLine_BillingCodeString.visible(isBillingClassificationEnabled);
    amountDetails.visible(isBillingClassificationEnabled);
    filterBillingClassification.visible(isBillingClassificationEnabled);

    if (isConfigurationkeyEnabled(configurationkeynum(FreeTextInvoiceCorrection))
        // <GBR>
        || BrazilParameters::isEnabled()
        // </GBR>
        )
    {
        filterRelatedInvoices = false;
    }

    if (element.args().menuItemName() == menuitemDisplayStr(CustFreeInvoiceAssignedToMeListPage))
    {
        element.applyAssignedToMe();
    }

    custInvoiceTableWorkflowQueueView_QueueName.visible(WorkflowWorkItemQueue::getNumberOfQueues(classStr(CustFreeInvoiceWorkflowDocument)) > 0);

    directiveTabPage_PSN.visible(isFrenchRegulatoryEnabled);
    maintainTitre_PSN.visible(isFrenchRegulatoryEnabled);

    reverseCharge = ReverseCharge_W::newParameters(custInvoiceTable, custInvoiceLine);

    cvSetupCustVendCreditInvoicingAction.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoES, #isoSE, #isoPL]));

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TaxTransSource.visible(false);
        CustInvoiceLine_TaxGroup.visible(false);
        CustInvoiceLine_TaxItemGroup.visible(false);
        TaxGroup.visible(false);
        TaxItemGroup.visible(false);
    }

    if (TaxIntegrationUtils::isMultipleTaxIdEnabledForBusinessProcess(TaxIntegrationBusinessProcess::FTI))
    {
        if (TaxHideTaxIDFromGUIWhenMutipleVATIDEnabledFlight::instance().isEnabled())
        {
            // When support multiple VAT registration numbers parameter is enabled, tax id should be invisibile from the UI.
            custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, TaxId)).visible(false);
        }

        // When support multiple VAT registration numbers parameter is enabled, it should not edit tax id from the UI.
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, TaxId)).allowEdit(false);

    }

    // <GTH>
    element.initDesign_TH();
    // </GTH>

    custInvoiceTable_DS.object(fieldNum(CustInvoiceTable, VatDueDate_W)).visible(TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled());

    boolean projOperationsIntegrationFeatureIsNotEnabled = !ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, ProjId)).visible(projOperationsIntegrationFeatureIsNotEnabled);
    CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, ProjCategoryId)).visible(projOperationsIntegrationFeatureIsNotEnabled);
    CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, ProjFundingSource)).visible(projOperationsIntegrationFeatureIsNotEnabled);

    this.updateControlsForOverrideSalesTax();

    boolean areFinTagsEnabled = this.areFinTagsEnabled();
    if (!areFinTagsEnabled)
    {
        FinancialTags.visible(areFinTagsEnabled);
        FinTagFastTab.visible(areFinTagsEnabled);
    }

    Query query;
    boolean isCustFreeTextInvoiceInquiryForm = false;
    CustInvoiceTable custInvoiceTableRecord;

    if (element.args().dataset() == tableNum(CustInvoiceTable))
    {
        custInvoiceTableRecord = element.args().record();
    }

    if (isFTIPerformanceFeatureEnabled && custInvoiceTableRecord)
    {
        AllOpenPostedField.visible(true);    
        postedCriteria = CustInvoiceTable_ds.query().dataSourceName(CustInvoiceTable_ds.name()).addRange(fieldNum(CustInvoiceTable, Posted));
        AllOpenPostedField.selection(AllOpenPosted::All);
        FilterInvoiceDate.visible(true);    
        invoiceDateCriteria = CustInvoiceTable_ds.query().dataSourceName(CustInvoiceTable_ds.name()).addRange(fieldNum(CustInvoiceTable, InvoiceDate));
        FilterInvoiceDate.selection(CustInvoiceDateRange::Custom);
    }

    if (isFTIPerformanceFeatureEnabled && !custInvoiceTableRecord)
    {
        isCustFreeTextInvoiceInquiryForm = element.args().menuItemName() == menuItemDisplayStr(CustFreeTextInvoiceInquiryForm);
        postedCriteria = CustInvoiceTable_ds.query().dataSourceName(CustInvoiceTable_ds.name()).addRange(fieldNum(CustInvoiceTable, Posted));
        invoiceDateCriteria = CustInvoiceTable_ds.query().dataSourceName(CustInvoiceTable_ds.name()).addRange(fieldNum(CustInvoiceTable, InvoiceDate));

        FilterInvoiceDate.visible(true);
        AllOpenPostedField.visible(true);

        if (isCustFreeTextInvoiceInquiryForm)
        {
            query = CustInvoiceTable_ds.query();
            element.updateQueryForMenu(query);
            FilterInvoiceDate.setValueForInquiryForm();
        }
        else
        {
            FilterInvoiceDate.setValue();
            FilterInvoiceDate.selectionChanged();
        }

        element.applyFilterOnPosted();
    }

}

private void applyFilterOnPosted()
{
    AllOpenPosted allOpenPostedValue;
    allOpenPostedValue = AllOpenPosted::Open;

    allOpenPostedField.selection(allOpenPostedValue);
    allOpenPostedField.selectionChanged();
}

private void updateQueryForMenu(Query _query)
{
    TableId sourceTable;
    sourceTable = element.args().dataset();
    Debug::assert(sourceTable != tableNum(CustInvoiceTable));

    element.promptSysQueryForm(_query);
}

private void promptSysQueryForm(Query _query)
{
    QueryBuildDataSource ds;
    ds = SysQuery::findOrCreateDataSource(_query, tableNum(CustInvoiceTable));

    date todayDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    date startDate = element.getFiscalStartDate(todayDate);

    ds.rangeField(fieldNum(CustInvoiceTable, InvoiceDate)).value(SysQuery::range(startDate, todayDate));
    ds.addRange(fieldNum(CustInvoiceTable, InvoiceAccount));
    ds.addRange(fieldNum(CustInvoiceTable, InvoiceId));
    ds.addRange(fieldNum(CustInvoiceTable, CurrencyCode));
    ds.rangeField(fieldNum(CustInvoiceTable, Posted)).status(RangeStatus::Hidden);

    queryRun = new QueryRun(_query);

    if (!queryRun.prompt())
    {
        element.close();
    }

    CustInvoiceTable_ds.query(queryRun.query());
}

/// <summary>
/// This method returns the fiscal start date for the company
/// </summary>
/// <param name = "_todayDate">
/// Current date as per the system
/// </param>
private date getFiscalStartDate(date _todayDate)
{
    RecId fiscalCalendarRecId;
    date fiscalStartDate;
    fiscalCalendarRecId = Ledger::fiscalCalendar(CompanyInfo::current());
    if (!fiscalCalendarRecId)
    {
        fiscalStartDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) - 30;
    }
    else
    {
        fiscalStartDate = LedgerFiscalCalendar::findFirstDayOfYear(fiscalCalendarRecId, _todayDate);
    }

    return fiscalStartDate;
}

private boolean areFinTagsEnabled()
{
    return isFinTagConfigurationSetForCurrent
        && AccountsReceivableFinTagFlight::instance().isEnabled();
}

private void updateControlsForOverrideSalesTax()
{
    if (isTaxIntegrationEnabledForFTI)
    {
        CustInvoiceTable_ds.object(fieldNum(CustInvoiceTable, OverrideSalesTax)).visible(true);
        CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, OverrideSalesTax)).visible(true);
        CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, TaxGroup)).allowEdit(CustInvoiceLine.OverrideSalesTax);
        CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, TaxItemGroup)).allowEdit(CustInvoiceLine.OverrideSalesTax);
    }
    else
    {
        CustInvoiceTable_ds.object(fieldNum(CustInvoiceTable, OverrideSalesTax)).visible(false);
        CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, OverrideSalesTax)).visible(false);
        CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, TaxGroup)).allowEdit(true);
        CustInvoiceLine_ds.object(fieldNum(CustInvoiceLine, TaxItemGroup)).allowEdit(true);
    }
}

