        buttonCustPostFactureFreeTxtJob_RU.visible(false);
    }
}

/// <summary>
/// Refresh the form to display a new invoice.
/// </summary>
/// <param name = "_custInvoiceTable">
/// The free text invoice that was created.
/// </param>
public void invoiceCreated(CustInvoiceTable _custInvoiceTable)
{
    custInvoiceTable_ds.research();
    custInvoiceTable_ds.positionToRecord(_custInvoiceTable);

    if (tabPageGrid.isActivePage())
    {
        element.task(#taskSwitchToLineDetailsView);
    }

    this.putInEditMode();
}

