{
    amount_IN.visible(TaxParameters::checkTaxParameters_IN());
}

/// <summary>
/// Displays the <c>CustCustomFieldInputDialog</c> dialog if there are any custom fields that need to be displayed.
/// </summary>
/// <returns>
/// A false value if the dialog is displayed and cancel is clicked otherwise true.
/// </returns>
public boolean showCustomFieldDialog()
{
    boolean canShow = false;
    MenuFunction menuFunction;

    Args args;

    canShow = element.canShowInputDialog();
    if (canShow)
    {
        args = new Args();
        args.parm(custInvoiceTable.Posted == NoYes::Yes ? '1' : '0');
        args.record(custInvoiceLine);
        args.parmObject(mapCustCustomFieldElements);
        args.caller(element);

        menuFunction = new MenuFunction(menuitemActionStr(CustCustomFieldInputDialog),MenuItemType::Action);

        buttonCustomField.enabled(false);

        customFieldDialogOpen = true;
        element.setupCustomFieldButtonStatus();

        menuFunction.run(args);
    }
    else
    {
        customFieldDialogOkClicked = true;
    }

    customFieldDialogOpen = false;
    customFieldDialog = null;

    element.setupCustomFieldButtonStatus();

    return customFieldDialogOkClicked;
}

/// <summary>
/// Returns the appropriate document icon/symbol represented in string format for a given record.
/// </summary>
/// <param name="_common">
/// A table record.
/// </param>
/// <returns>
/// An icon symbol string.
/// </returns>
public display str showDocumentIcon(Common _common)
{
    return DocuRef::exist(_common.DataAreaId, _common.TableId, _common.RecId) ? 'GenericDocument' : 'PlannedDocument';
}

/// <summary>
/// Displays the attchments for the given table buffer.
/// </summary>
/// <param name = "_record"></param>
public void showDocuments(Common _record)
{
    if (_record)
    {
        Args args = new Args(formStr(DocuAttachment));

        args.caller(element);
        args.record(_record);

        FormRun formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

/// <summary>
/// Synchronizes the contents of the billingCodeLookup form-level variable with the billing code of the current invoice line.
/// </summary>
/// <remarks>
/// Reloads the record if necessary. If an invoice line has no billing code, empties the record buffer.
/// </remarks>
public void syncBillingCodeLookup()
{
    if (custInvoiceLine.BillingCode == "")
    {
        billingCodeLookup.clear();
        custBillingCodeVersion.clear();
    }
    else
    {
        if (custInvoiceLine.BillingCode != billingCodeLookup.BillingCode)
        {
            billingCodeLookup = CustBillingCode::findBillingCode(custInvoiceLine.BillingCode);
            custBillingCodeVersion = CustBillingCodeVersion::findByDate(billingCodeLookup.RecId, custInvoiceTable.InvoiceDate);
        }
    }
}

/// <summary>
///     Sets fields visible and changes the labels when Average VAT feature is active.
/// </summary>
private void updateDesign()
{
    noteFormat.visible(true);
    reference.visible(true);
    isRealizedVAT.visible(true);

    this.noteFormatEdit();
}

/// <summary>
/// Update form state based on the source document status
/// </summary>
/// <param name = "_sourceDocumentLineRecId">
/// The Id of the source document line.
/// </param>
/// <remarks>
/// Enable subledger journal entries button if the source document line is in a fully distributed state otherwise disable
/// </remarks>
public void updateFormState(RefRecId _sourceDocumentLineRecId)
{
    if (custInvoiceLine.SourceDocumentLine == _sourceDocumentLineRecId)
    {
        if (custInvoiceLine.AmountCur != 0)
        {
            accountingDistributionsLineButton.enabled(true);
        }
        else
        {
            accountingDistributionsLineButton.enabled(false);
        }
    }
}

private void updateMarkupDistribution_W()
{
    CustInvoiceLine localCustInvoiceLine;
    MarkupTrans localMarkupTrans;

    while select localMarkupTrans
        where localMarkupTrans.TransRecId == custInvoiceTable.RecId
           && localMarkupTrans.TransTableId == custInvoiceTable.TableId
    {
        SourceDocumentProcessorFacade::submitSourceDocumentLineImplementation(localMarkupTrans);
    }

    while select TableId from localCustInvoiceLine
        where localCustInvoiceLine.ParentRecId == custInvoiceTable.RecId
        join localMarkupTrans
            where localMarkupTrans.TransRecId == localCustInvoiceLine.RecId
               && localMarkupTrans.TransTableId == localCustInvoiceLine.TableId
    {
        SourceDocumentProcessorFacade::submitSourceDocumentLineImplementation(localMarkupTrans);
    }
}

public int task(int _taskId)
{
    if (_taskId == #taskEditRecord)
    {
        if (custInvoiceTable.WorkflowApprovalStatus == CustFreeInvoiceWFApprovalStatus::Approved && !custInvoiceTable.Posted)
        {
            // If the free text invoice is approved, the user can choose to either:
            // edit the "approved" invoice by selecting the yes button, open the details form in read-only mode
            // by clicking the No button, or cancel it by clicking the cancel button.
            DialogButton button = Box::yesNoCancel(literalstr("@AccountsReceivable:FTIWorkFlowEditConfirm"), DialogButton::Cancel, "@SYS328754");
            switch (button)
            {
                case DialogButton::Yes :
                    CustInvoiceTable::setWorkflowState(custInvoiceTable.RecId, CustFreeInvoiceWFApprovalState::NotSubmitted);
                    break;
                case DialogButton::No :
                    element.inViewMode();
                    break;
                case DialogButton::Cancel :
                    return -1;
            }
        }
    }

    int ret = super(_taskId);

    return ret;
}

/// <summary>
/// Filter invoices by billing classification.
/// </summary>
/// <param name="_billingClassification">
/// The filter base on the <c>BillingClassification</c> value.
/// </param>
public void filterBillingClassification(BillingClassification _billingClassification)
{
    if (_billingClassification == "")
    {
        qbrBillingClassification.value(queryValue(SysQuery::valueEmptyString()));
    }
    else
    {
        RefRecId custBillingClassificationRecId;

        custBillingClassificationRecId = CustBillingClassification::findByBillingClassification(_billingClassification).RecId;
        qbrBillingClassification.value(queryValue(custBillingClassificationRecId));
    }

    custInvoiceTable_ds.executeQuery();
}

void allowEnable()
{
    boolean enableButtons;

    if (custInvoiceTable_ds.cursor())
    {
        enableButtons = true;
    }

    custInvoiceTableTotals.enabled(enableButtons);
    markupTrans.enabled(enableButtons);
    taxTransSource.enabled(enableButtons);
    detailsAction.enabled(enableButtons);

    relatedInformationAction.enabled(enableButtons);

    attachments.enabled(enableButtons);

    if ((isConfigurationkeyEnabled(configurationKeyNum(FreeTextInvoiceCorrection))
        // <GBR>
        || BrazilParameters::isEnabled()
        // </GBR>
        ) && !filterRelatedInvoices)
    {
        if (enableButtons && custRelatedInvoice)
        {
            viewInvoice.enabled(true);
            allInvoice.enabled(false);
        }
        else
        {
            viewInvoice.enabled(false);
        }
    }

    if (CustInvoiceTable::isInActiveWorkflow(custInvoiceTable.RecId))
    {
        custInvoiceTable_ds.allowDelete(false);
    }

    // <GEERU>
    if (countryRegion_EEU)
    {
        custFreeCreditNote_W.enabled(custInvoiceTable.isEditable());
    }
    // </GEERU>

    if (countryRegion_LTLV)
    {
        custInvoiceTable_DS.object(fieldNum(CustInvoiceTable, DocNumberingCode_LT)).allowEdit(CustInvoiceTable.ManualNumbering_W);
    }

    // <GTH>
    if (CustInvoiceJourAdjustment_TH.visible())
    {
        CustInvoiceJourAdjustment_TH.enabled(custInvoiceTable.custInvoiceJour().RecId);
    }
    // </GTH>
}

void allowEdit_MultiSelect()
{
    CustInvoiceTable custInvoiceTableLocal;
    boolean setEnabled = true;

    setEnabled = true;
    //go through all the selected records
    custInvoiceTableLocal = custInvoiceTable_ds.getFirst(1,false);

    if (custInvoiceTableLocal)
    {
        while (custInvoiceTableLocal && setEnabled)
        {
            if (custInvoiceTableLocal.Posted || CustInvoiceTable::isInActiveWorkflow(custInvoiceTableLocal.RecId))
            {
                setEnabled = false;
            }

            custInvoiceTableLocal = custInvoiceTable_ds.getNext();
        }
    }

    else
    //On form load, no records are marked initially
    {
        if (custInvoiceTable.Posted || CustInvoiceTable::isInActiveWorkflow(custInvoiceTable.RecId))
        {
            setEnabled = false;
        }
    }

    custInvoiceTable_ds.allowDelete(setEnabled);

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        setEnabled = custInvoiceTable.Posted;
        custInvoiceTableLocal = custInvoiceTable_ds.getFirst(1,false);
        if (custInvoiceTableLocal)
        {
            setEnabled = true;
            while (custInvoiceTableLocal && setEnabled)
            {
                if (! custInvoiceTableLocal.Posted)
                {
                    setEnabled = false;
                }
                custInvoiceTableLocal = custInvoiceTable_ds.getNext();
            }
        }
        acceptanceReport_RU.enabled(setEnabled);
    }
    // </GEERU>
}

public void applyAssignedToMe()
{
    var queryAssignedToMe = new Query(queryStr(FreeTextInvoiceAssignedToMe));

    var rangeAssignee = queryAssignedToMe.dataSourceTable(tableNum(WorkflowWorkItemTable))
                                         .addRange(fieldNum(WorkflowWorkItemTable, UserId));
    rangeAssignee.value(queryValue(curUserId()));
    rangeAssignee.status(RangeStatus::Locked);

    SysQuery::copyDataSource(queryAssignedToMe.dataSourceTable(tableNum(CustInvoiceTable)),
                             custInvoiceTable_ds.queryBuildDataSource());

    custInvoiceTable_ds.executeQuery();
}

