public RecordSortedList getSelectedRecords()
{
    RecordSortedList recordSortedList;
    CustInvoiceTable currentCustInvoiceTable;

    recordSortedList = new RecordSortedList(tableNum(CustInvoiceTable));
    recordSortedList.sortOrder(fieldNum(CustInvoiceTable,RecId));

    currentCustInvoiceTable = Global::getFirstSelection(custInvoiceTable_ds);

    while (currentCustInvoiceTable)
    {
        recordSortedList.ins(currentCustInvoiceTable);
        currentCustInvoiceTable = custInvoiceTable_ds.getNext() as CustInvoiceTable;
    }

    return recordSortedList;
}

public void printInvoice(
    PrintCopyOriginal _action,
    boolean _sendElectronocally = false,
    boolean _acceptanceReport_RU = false,
    boolean _substitutionInvoice_TH = false,
    boolean _adjustmentInvoice_TH = false,
    boolean _print = true)
{
    CustPrintOutInvoice custPrintOutInvoice;
    NoYes usePrintManagement;

    // <GEERU>
    CustInvoiceJour custInvoiceJour;
    CustInvoiceTable currentCustInvoiceTable;
    int loop;
    RecordSortedList custInvoiceJourList;
    Args args = new Args();
    RecordSortedList rsl = element.getSelectedRecords();

    if (_acceptanceReport_RU)
    {
        custInvoiceJourList = new RecordSortedList(tableNum(CustInvoiceJour));
        custInvoiceJourList.sortOrder(fieldNum(CustInvoiceJour, RecId));

        for (loop = rsl.len(); loop > 0; loop--)
        {
            rsl.next(currentCustInvoiceTable);
            custInvoiceJour = currentCustInvoiceTable.custInvoiceJour();
            if (custInvoiceJour.RecId)
            {
                custInvoiceJourList.ins(custInvoiceJour);
            }
        }

        args.object(custInvoiceJourList);
        new MenuFunction(menuitemOutputStr(SalesAcceptanceReport_RU), MenuItemType::Output).run(args);
    }
    //<GTH>
    else if (TaxThaiGovCertificationFeatureChecker::isSubstitutionAdjustmentEnabled()
        && (_substitutionInvoice_TH || _adjustmentInvoice_TH || (_action == PrintCopyOriginal::Copy)))
    {
        this.printInvoices_TH(rsl, args, _substitutionInvoice_TH, _adjustmentInvoice_TH);
    }
    //</GTH>
    else
    {
        // </GEERU>
        if (_action == PrintCopyOriginal::OriginalPrint)
        {
            usePrintManagement = NoYes::Yes;
        }

        custPrintOutInvoice = CustPrintOutInvoice::construct();

        // setting custPrintOutInvoice in batch state so that no printer setup dialog appear and it use default settings
        custPrintOutInvoice.parmIsBatched(true);
        // <GEERU>
        custPrintOutInvoice.parmPrintFormLetter_RU(NoYes::Yes);
        // </GEERU>
        custPrintOutInvoice.printOut(
            NoYes::Yes,
            element.getSelectedRecords(),
            NoYes::No,
            Printout::Current,
            usePrintManagement,
            _action,
            _sendElectronocally,
            false,
            NoYes::No,
            _print);
        // <GEERU>
    }
    // </GEERU>
}

/// <summary>
/// Handles printing of substitution/adjustment free text invoices for Thailand.
/// </summary>
/// <param name = "_rsl">
/// List of records selected for printing.
/// </param>
/// <param name = "_args">
/// Printing properties, such as record ID and men items.
/// </param>
/// <param name = "_substitutionInvoice_TH">
/// True if substitution is printed.
/// </param>
/// <param name = "_adjustmentInvoice_TH">
/// True if adjustment is printed.
/// </param>
private void printInvoices_TH(
    RecordSortedList _rsl,
    Args _args,
    boolean _substitutionInvoice_TH = false,
    boolean _adjustmentInvoice_TH = false)
{
    for (int loop = _rsl.len(); loop > 0; loop--)
    {
        CustInvoiceTable currentCustInvoiceTable;
        _rsl.next(currentCustInvoiceTable);
        CustInvoiceJour custInvoiceJour = currentCustInvoiceTable.custInvoiceJour();

        if (custInvoiceJour.RecId)
        {
            _args.record(custInvoiceJour);
            if (_substitutionInvoice_TH)
            {
                new MenuFunction(menuitemOutputStr(FreeTextInvoiceSubstitution_TH), MenuItemType::Output).run(_args);
            }
            else if (_adjustmentInvoice_TH)
            {
                new MenuFunction(menuitemOutputStr(FreeTextInvoiceAdjustment_TH), MenuItemType::Output).run(_args);
            }
            else
            {
                new MenuFunction(menuitemOutputStr(FreeTextInvoiceCopy), MenuItemType::Output).run(_args);
            }
        }
    }
}

/// <summary>
/// Performs lookup for <c>CustInvoiceJour</c>
/// </summary>
/// <param name="_control">
/// The control to require lookup
/// </param>
/// <param name="_queryRange">
/// The range of query.
/// </param>
/// <param name="_custAccount">
/// The account of customer.
/// </param>
public void performCustInvoiceJourLookup_MY(
    FormStringControl _control,
    Range _queryRange,
    CustAccount _custAccount)
{
    custInvoiceJourLookupController.parmQueryRange(_queryRange);
    custInvoiceJourLookupController.parmCustAccount(_custAccount);
    custInvoiceJourLookupController.performLookup(_control);
}

/// <summary>
///     Updates the controls used for selecting source invoice transaction.
/// </summary>
public void updateSourceRefControlsAfterLookup_MY()
{
    if (custInvoiceJourLookupController.parmInvoiceInfo())
    {
        [custInvoiceLine_W.RefInvoiceId_MY, custInvoiceLine_W.RefInvoiceDate_MY] = custInvoiceJourLookupController.parmInvoiceInfo();

        // After updated, clear the InvoiceInfo container of CustInvoiceJourLookupController to prepare for next lookup.
        custInvoiceJourLookupController.parmInvoiceInfo(conNull());
    }
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    var ftiHeader = _menuOptions.getOptionsForEntity(tableStr(FreeTextInvoiceHeaderEntity));

    if (ftiHeader)
    {
        ftiHeader.includeDefault(false);
    }

    OfficeTemplateExportMenuItem ftiMenuItem;

    if (CustParameters::isBillingClassificationEnabled())
    {
        ftiMenuItem = OfficeTemplateExportMenuItem::construct(OfficeAppApplicationType::Excel, resourceStr(FreeTextInvoiceEntityPSNTemplate), custFreeTextInvoiceOfficeMenuId);
    }
    else
    {
        ftiMenuItem = OfficeTemplateExportMenuItem::construct(OfficeAppApplicationType::Excel, resourceStr(FreeTextInvoiceEntityTemplate), custFreeTextInvoiceOfficeMenuId);
    }

    ftiMenuItem.displayName("@AccountsReceivable:CustFreeInvoiceOpenInExcel");
    ftiMenuItem.applyRecordFilter(false);

    _menuOptions.customMenuItems().addEnd(ftiMenuItem);
}

Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);
    if (_menuItem.id() == custFreeTextInvoiceOfficeMenuId)
    {
        var lineEntityName = tableStr(FreeTextInvoiceLineEntity);
        var headerEntityName = tableStr(FreeTextInvoiceHeaderEntity);

        // Create lines filter
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);

        var lineFilter = lineFilterBuilder.and(
                                            lineFilterBuilder.areEqual(fieldStr(FreeTextInvoiceLineEntity, ParentRecId), custInvoiceTable.RecId),
                                            lineFilterBuilder.areEqual(fieldStr(FreeTextInvoiceLineEntity, DataAreaId), custInvoiceTable.dataAreaId));

        filtersToApply.insert(lineEntityName, lineFilter);

        // Create header filter
        ExportToExcelFilterTreeBuilder headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);

        var headerFilter = headerFilterBuilder.and(
                                            headerFilterBuilder.areEqual(fieldStr(FreeTextInvoiceHeaderEntity, InvoiceIdentifier), custInvoiceTable.RecId),
                                            headerFilterBuilder.areEqual(fieldStr(FreeTextInvoiceHeaderEntity, DataAreaId), custInvoiceTable.dataAreaId));

        filtersToApply.insert(headerEntityName, headerFilter);

        return filtersToApply;
    }
    return filtersToApply;
}

