{
    //Required by interface not important to this scenario
}

private void initDesign_TH()
{
    boolean enableSubstitutionInvoices = TaxThaiGovCertificationFeatureChecker::isSubstitutionAdjustmentEnabled();

    ViewSubstitution_TH.visible(enableSubstitutionInvoices);
    ViewAdjustment_TH.visible(enableSubstitutionInvoices);
    CustInvoiceJourAdjustment_TH.visible(enableSubstitutionInvoices);
}

/// <summary>
/// Update project details to project accounting distributions if both project id and category id exists.
/// </summary>
private void updateProjectDetails()
{
    custInvoiceLine_ds.rereadReferenceDataSources();
    custInvoiceLine.write();

    ProjTable projTable = ProjTable::find(custInvoiceLine.ProjId);
    if (ProjInvoiceTable::isSalesCurrencyLocked(projTable.ProjInvoiceProjId))
    {
        CustCurrencyCode currencyCode = ProjInvoiceTable::find(projTable.ProjInvoiceProjId).CurrencyId;
        if (currencyCode != custInvoiceTable.CurrencyCode)
        {
            custInvoiceTable.CurrencyCode = currencyCode;
            if (CustInvoiceTable::promptConvertCurrencyCode())
            {
                custInvoiceTable.convertCurrencyCode(currencyCode);
            }
            custInvoiceTable.write();
        }
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, CurrencyCode)).allowEdit(false);
    }
    else
    {
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, CurrencyCode)).allowEdit(true);
    }
    // Activates the workflow controls for free text invoice.
    CustInvoiceLine_ds.written();
    custInvoiceLine_ds.reread();
    custInvoiceLine_ds.refresh();
    custInvoiceLine_ds.rereadReferenceDataSources();
}

private void refreshDataSourceForTaxIntegration()
{
    if (isTaxIntegrationEnabledForFTI)
    {
        CustInvoiceTable_ds.reread();
        CustInvoiceLine_ds.research(true);
    }
}

