public display str displayInvoiceNumber(CustInvoiceTable _custInvoiceTable)
{
    return _custInvoiceTable.InvoiceId == "" ? "@SYS11670" : _custInvoiceTable.InvoiceId;
}

public void displayCorrectionState(CustInvoiceTable _custInvoiceTable, boolean _clearMessage = false)
{
    if (correctionStatusId)
    {
        Message::Remove(correctionStatusId);
        correctionStatusId = 0;
    }

    if (!_clearMessage)
    {
        var custInvoiceTableLocal = custInvoiceTable_ds.getFirst(1, true);
        var localInvoiceTableCheck = custInvoiceTableLocal.RecId == _custInvoiceTable.RecId || !custInvoiceTableLocal;

        if (localInvoiceTableCheck && custRelatedInvoice && !custInvoiceTable_DS.getNext())
        {
            switch (custRelatedInvoice.InvoiceAssociationType)
            {
                case InvoiceAssociationType::CorrectedInvoice:
                    correctionStatusId = Message::Add(MessageSeverity::Warning, strFmt("@SYS76498",
                        custInvoiceTable.Posted ? "@SYS99716" : "@AccountsReceivable:CorrectingInvoice",
                        CustInvoiceTable::findRecId(custRelatedInvoice.ParentCustInvoice).InvoiceId));
                    break;

                case InvoiceAssociationType::AdjustingInvoice:
                    correctionStatusId = Message::Add(MessageSeverity::Warning, strFmt("@SYS76498",
                        custInvoiceTable.Posted ? "@AccountsReceivable:CanceledInvoice" : "@SYS300090",
                        CustInvoiceTable::findRecId(custRelatedInvoice.ParentCustInvoice).InvoiceId));
                    break;
            }
        }
    }
}

